(688, '<h3>Craigslist Scanner</h3>\r\n<form action=<?php echo htmlspecialchars($_SERVER["PHP_SELF"]); ?> method="post">\r\n    <p>keywords separated by plus signs:  red+bike+training+wheels</p>\r\n	query:\r\n	<input type="text" name="query">\r\n	<br>\r\n\r\n	<input type="submit" name="method" value ="search">\r\n</form>\r\n\r\n<?php\r\n//http://stackoverflow.com/questions/5164930/fatal-error-maximum-execution-time-of-30-seconds-exceeded\r\nini_set(''max_execution_time'', 300);\r\n//300 seconds = 5 minutes\r\n\r\nif (isset($_POST[''method''])) {\r\n\r\n    $html = file_get_contents(''http://louisville.craigslist.org/search/gra?query='' . $_POST[''query'']);\r\n    //Create a new DOM document\r\n    $dom = new DOMDocument;\r\n    $array_of_links = array();\r\n    $final_array = array();\r\n\r\n    //Parse the HTML. The @ is used to suppress any parsing errors\r\n    //that will be thrown if the $html string isn''t valid XHTML.\r\n    @$dom -> loadHTML($html);\r\n\r\n    //Get all links. You could also use any other tag name here,\r\n    //like ''img'' or ''table'', to extract other tags.\r\n    $links = $dom -> getElementsByTagName(''a'');\r\n\r\n    //Iterate over the extracted links and display their URLs\r\n    foreach ($links as $link) {\r\n        //Extract and show the "href" attribute.\r\n        //echo $link -> nodeValue;\r\n        //echo $link -> getAttribute(''href''), ''<br>'';\r\n        array_push($array_of_links, $link -> getAttribute(''href''));\r\n    }\r\n\r\n    //craigslist.org//count($array_of_links)\r\n    $pertinent_links = array();\r\n\r\n    for ($i = 0; $i < count($array_of_links); $i++) {\r\n        if (stripos($array_of_links[$i], ''/grd/'') === 0) {\r\n            array_push($pertinent_links, $array_of_links[$i]);\r\n        }\r\n    }\r\n    $count = 0;\r\n    for ($j = 0; $j < count($pertinent_links); $j++) {\r\n        $result = getOne($pertinent_links[$j]);\r\n        array_push($final_array, $result);\r\n        $count = $j;\r\n\r\n    }\r\n\r\n   // echo("<br>LAST INDEX COUNT:" . $count);\r\n\r\n    //echo("<pre>");print_r($array_of_posted_dates); echo("</pre>");\r\n    //$res = getOne($array_of_links[110]);\r\n    //print_r($res);\r\n   // echo(''<br>got results:'');\r\n\r\n    echo("<pre>");\r\n    print_r($final_array);\r\n    echo("</pre>");\r\n    //echo("<hr><p>Total List of Links:</p>");\r\n\r\n    //echo("<pre>");\r\n   // print_r($array_of_links);\r\n   // echo("</pre>");\r\n\r\n}\r\n\r\n/*\r\n *  <span id="titletextonly">Toro Riding Lawn Mower</span> - <span class="price">$1000</span>\r\n * <meta name="description" content="Selling this riding lawn mower. 6 years old, only used for one Year. Has been in storage for 5. New Battery. MSRP $1,699. 42cutting deck. https://www.toro.com/home/tractors/lx/426lx_specs.html $1,000 ">\r\n *\r\n */\r\n\r\n$ridingLawnMowers = "riding+lawn+mower";\r\n$peacocks = "peacocks";\r\n$query = $peacocks;\r\n\r\n$html = file_get_contents(''http://louisville.craigslist.org/search/gra?query=riding+lawn+mower'');\r\n\r\nfunction getOne($link) {\r\n    $result = array();\r\n    $start_post = false;\r\n    $entry = array();\r\n\r\n    //echo("<br>LINK:".$link);\r\n    $html = file_get_contents(''http://louisville.craigslist.org'' . $link);\r\n    //Create a new DOM document\r\n    $dom = new DOMDocument;\r\n    @$dom -> loadHTML($html);\r\n\r\n    $description = getMeta($dom);\r\n    foreach (explode("\\n", $dom->textContent) as $line) {\r\n        //echo("<br>".$line);\r\n        ///check if word is there\r\n        // words[''post id:'', ''posted:'', ''updated:'' ]\r\n        if (strpos($line, ''favorite this post'') !== false) {\r\n            $start_post = true;\r\n        }\r\n        if (strpos($line, ''email to friend'') !== false) {\r\n            $start_post = false;\r\n        }\r\n        if ($start_post) {\r\n            //echo( trim($line));\r\n        }\r\n\r\n        if (strpos($line, ''post id:'') !== false) {\r\n            $post_id = str_replace("post id:", '''', $line);\r\n            if (!is_null($post_id)) {\r\n                array_push($result, $post_id);\r\n            }\r\n\r\n        }\r\n        if (strpos($line, ''posted:'') !== false) {\r\n            $posted_date = str_replace("posted:", '''', $line);\r\n            if (!is_null($posted_date)) {\r\n                array_push($result, $posted_date);\r\n            }\r\n            $myTime = strtotime($posted_date);\r\n            $timepassed = "posted " . ago($myTime);\r\n            array_push($result, $timepassed);\r\n\r\n        }\r\n        if (strpos($line, ''updated:'') !== false) {\r\n            // echo "<br>" . $line;\r\n            $updated = str_replace("updated:", '''', $line);\r\n            if (!is_null($updated)) {\r\n                array_push($result, $updated);\r\n            }\r\n            $myTime = strtotime($updated);\r\n            $timepassed = "updated  " . ago($myTime);\r\n            array_push($result, $timepassed);\r\n        }\r\n        //price\r\n        if (strpos($line, ''price'') !== false) {\r\n            //$post_id = str_replace("post id:", '''', $line);\r\n            //if(!is_null($post_id )){\r\n            array_push($result, $line);\r\n            //}\r\n\r\n        }\r\n\r\n    }\r\n    array_push($result, $description);\r\n    return $result;\r\n}\r\n\r\nfunction getMeta($doc) {\r\n    $nodes = $doc -> getElementsByTagName(''title'');\r\n\r\n    //get and display what you need:\r\n    $title = $nodes -> item(0) -> nodeValue;\r\n\r\n    $metas = $doc -> getElementsByTagName(''meta'');\r\n\r\n    $imgs = $doc -> getElementsByTagName(''img'');\r\n    //print_r($imgs);\r\n\r\n    for ($i = 0; $i < $metas -> length; $i++) {\r\n        $meta = $metas -> item($i);\r\n        if ($meta -> getAttribute(''name'') == ''description'')\r\n            $description = $meta -> getAttribute(''content'');\r\n        //if($meta->getAttribute(''name'') == ''keywords'')\r\n        //$keywords = $meta->getAttribute(''content'');\r\n    }\r\n\r\n    //echo(''"</br>"'');\r\n    // echo "</br>Title: $title". ''<br/><br/>'';\r\n    //echo "</br>Description: $description". ''<br/><br/>'';\r\n    return $description;\r\n}\r\n\r\nfunction ago($time) {\r\n    $periods = array("second", "minute", "hour", "day", "week", "month", "year", "decade");\r\n    $lengths = array("60", "60", "24", "7", "4.35", "12", "10");\r\n\r\n    $now = time();\r\n\r\n    $difference = $now - $time;\r\n    $tense = "ago";\r\n\r\n    for ($j = 0; $difference >= $lengths[$j] && $j < count($lengths) - 1; $j++) {\r\n        $difference /= $lengths[$j];\r\n    }\r\n\r\n    $difference = round($difference);\r\n\r\n    if ($difference != 1) {\r\n        $periods[$j] .= "s";\r\n    }\r\n\r\n    return "$difference $periods[$j] ago ";\r\n}\r\n?>', 'craigslist scanner', 'php'),
