(705, '<html>\r\n	<head>\r\n		<!-- <script src="module.js"></script> -->\r\n	</head>\r\n	<body>\r\n		<div id=''myHTML''>\r\n			&nbsp;\r\n		</div>\r\n		<script>\r\n			function makeModule(args) {\r\n				var MODULE = ( function() {\r\n						var obj = {};\r\n						//---PRIVATE -----//\r\n						var privateProperty = "Module Maker 3000";\r\n						function privateMethod() {\r\n							console.log(''access to private method...'');\r\n							return "access to private method html";\r\n						}\r\n\r\n						//---PUBLIC----//\r\n						obj.id = "default id";\r\n						obj.name = "default name";\r\n\r\n						if ( typeof args == ''object'') {\r\n							obj.id = ( typeof args[''id''] != ''undefined'') ? args[''id''] : "no-id-argument";\r\n							obj.name = ( typeof args[''name''] != ''undefined'') ? args[''name''] : "no-name-argument";\r\n\r\n						}\r\n\r\n						obj.publicProperty = 1;\r\n\r\n						obj.publicMethod = function() {\r\n							// ...\r\n						};\r\n						obj.html = function() {\r\n							html = "<div>";\r\n							html += privateProperty;\r\n							html += "example text</br>";\r\n							html += privateMethod();\r\n							html += "</div>";\r\n							return html;\r\n						};\r\n						return obj;\r\n					}());\r\n				return MODULE;\r\n			}\r\n\r\n			/////////////////////////////////////\r\n			var args = {\r\n				"id" : "foo",\r\n				"name" : "bar"\r\n			};\r\n			var module = makeModule(args);\r\n			var myHTML = document.getElementById(''myHTML'');\r\n			myHTML.innerHTML = module.html();\r\n			//FAIL-module.privateMethod();\r\n			//FAIL - console.log(module.privateVariable);\r\n			console.log(module.id);\r\n			console.log(module.name);\r\n		</script>\r\n	</body>\r\n</html>', 'module maker advanced with args', 'javascript'),
