(724, '//MODAL ENTER KEY detection\r\nvar CURRENT_RECORD_ID = null;\r\nvar save_funtion = null;\r\nvar edit_function = null;\r\nvar isCreating = false;\r\nvar isEditing = false;\r\n\r\n function checkKeyPress(e){\r\n   //alert(e);\r\n   e = e || window.event;\r\n   if(e.keyCode == ''13''){\r\n     console.log(''enter...'');\r\n     if(isCreating){\r\n        console.log(''is creating:'' + CURRENT_RECORD_ID);\r\n       save_function(CURRENT_RECORD_ID);\r\n       $(''.modal-footer .btn'').click();\r\n     }\r\n     if(isEditing){\r\n       console.log(''is editing:'' + CURRENT_RECORD_ID);\r\n       edit_function(CURRENT_RECORD_ID);\r\n       $(''.modal-footer .btn'').click();\r\n     }\r\n   }else{\r\n     console.log(''keypress:''+e.keyCode);\r\n   }\r\n }\r\n\r\n$(''.modal-footer .btn'').click(function(){\r\n   isCreating = false;\r\n   isEditing = false;\r\n});\r\n\r\n//IN THE VIEW THIS CODE IS NEEDED.\r\n/*\r\n//enter keypress -----------------\r\ndocument.onkeydown = checkKeyPress ;\r\nedit_function = save_edit;\r\nsave_function = save_create;\r\n//requires:   CURRENT_RECORD_ID = TABLE_DATA[i][''id'']; AND   isEditing = true; in rowClick(i);\r\n//isCreating = true; in $(''.create_btn'').click(function()\r\n// set isEditing and isCreating false after edit,after create, after cancel.\r\n//------------------------------------\r\n*/\r\n', 'detect keypress enter globally and execute contextual action.\r\n', 'javascript'),
