(728, '<html>\r\n<head>\r\n	<script src="00_lib/js/jquery-1.11.2.min.js"> </script>\r\n</head>\r\n\r\n\r\n<script type="text/javascript">\r\n  $( "input[type=''checkbox''], input[type=''radio'']" ).on( "click", showValues );\r\n  function showValues(){\r\n    console.log(''show calues..........................'');\r\n  }\r\n        function calculateSquareFootage() {\r\n            if($("#length").val() != '''' && $("#width").val() != '''' && !isNaN($("#length").val()) && !isNaN($("#width").val())) {\r\n                $("#sqfeet").val(Math.abs($("#length").val() * $("#width").val()));\r\n            } else {\r\n                $("#sqfeet").val('''');\r\n            }\r\n        }\r\n        function calculateNumberOfPlants() {\r\n            if($("#sqfeet").val() != '''' && !isNaN($("#sqfeet").val()) && $("#spc").val() != '''' && !isNaN($("#spc").val())) {\r\n                var spacing = Math.abs($("#spc").val());\r\n                var sqFeet = Math.abs($("#sqfeet").val());\r\n                console.log(''detect a checkbox'');\r\n                $.each($(".unit"), function(i, e) {\r\n                          if (e.name == "authorization") {\r\n                					//handle radio group\r\n                					$("input[name*=authorization]:checked").each(function() {\r\n                						console.log(e.name + "--"+  $(this).val() );\r\n                						newUser.authorization = $(this).val();\r\n                					});\r\n                				}\r\n                    });\r\n\r\n\r\n\r\n                // $( "select" ).on( "change", showValues );\r\n/////////////////////////////////////\r\n  //switch($("input[@name=unit]:checked").val()) {\r\n                switch($("input[name*=unit]:checked").val()) {\r\n                    case ''in'':\r\n                        $("#plants").val(Math.round((144 / (spacing * spacing * 0.866)) * sqFeet));\r\n                        break;\r\n                    case ''ft'':\r\n                        spacing = spacing * 12