(735, '\r\n  <style>\r\n  /*SLID TOGGLE PANEL */\r\n  #slide-panel-1 {\r\n    width: 200px;\r\n    min-height: 500px;\r\n    height:100%;\r\n    background: #ccc;\r\n    opacity:.8;\r\n    position:fixed;\r\n    top:0px;\r\n    left:0px;\r\n  }\r\n  </style>\r\n<div id="slide-panel-1" class="slide_toggle">\r\n	<button class="slide-toggle-btn">close</button>\r\n</div>\r\n \r\n<script>\r\n// SLIDE TOGGLE PANEL\r\n$( document ).click(function(event) {\r\n	  var x = event.target;\r\n	  console.log(x.className);\r\n	  //prevent touching the panel itself to toggle the slide.\r\n	  if(x.className != "slide_toggle"){\r\n	  	 $( ".slide_toggle" ).toggle( "slide" );\r\n	  } \r\n});\r\n$(document).ready(function(){\r\n	//make the slide_toggle close on page load.\r\n	 $( ".slide_toggle" ).toggle( "slide" );\r\n});\r\n</script>', 'slide toggle panel', 'javascript'),
(736, '<!DOCTYPE html>\r\n<html>\r\n<head lang="en">\r\n	<meta http-equiv="X-UA-Compatible" content="IE=edge">\r\n	<meta charset="utf-8">\r\n	<title>HashNavigation</title>\r\n<style>\r\n.hashSection{display:none;width:100%;border:solid 1px #000;background-color:#ddd;height:100px;}\r\n.visible{display:block;}\r\n.hidden{display:none;}\r\n</style>	 \r\n</head>\r\n<body >\r\n<button onclick="navSection(''a'')">A</button>\r\n<button onclick="navSection(''b'')">B</button>\r\n<button onclick="navSection(''c'')">C</button>\r\n<div id="a" class="hashSection"></div>\r\n<div id="b" class="hashSection"></div>\r\n<div id="c" class="hashSection"></div>\r\n<script src="js/jquery.min.js"></script>\r\n<script> \r\nfunction navSection(sectionName){\r\n	window.location.hash = sectionName+''/'' ;\r\n	 $(window).trigger(''hashchange'');\r\n}\r\n$(window).on(''hashchange'', function(){\r\n		//render(decodeURI(window.location.hash));\r\n		$(".hashSection").removeClass(''visible'');\r\n		var section = window.location.hash.split(''/'')[0];\r\n		$(section).addClass(''visible'');\r\n		$(section).html(''This is section ''+section+''!'');\r\n});\r\n</script>\r\n</body>\r\n</html>', 'hash navigation  SPA most basic hashtag navigation.', 'javascript'),
(737, '<!DOCTYPE html>\r\n<html>\r\n<head lang="en">\r\n	<meta http-equiv="X-UA-Compatible" content="IE=edge">\r\n	<meta charset="utf-8">\r\n	<link rel=''stylesheet'' type=''text/css'' href=''./css/bootstrap.min.css''>\r\n	<title>Hashville!</title>\r\n<style>\r\n.hashSection{display:none; }\r\n.visible{display:block;}\r\n</style>	 \r\n</head>\r\n <body >\r\n	<div class="container">\r\n		 <h3>handlemonkey</h3>\r\n\r\n			<div id="monkey"></div> \r\n	</div><!-- end container -->\r\n\r\n<script src="js/jquery.min.js"></script>\r\n<script src="js/bootstrap.min.js"></script>\r\n<script src="js/handlebars.min.js"></script>\r\n<script src="js/hashville.js"></script>\r\n\r\n\r\n<script> \r\nvar TEMPLATE_OBJ = null;\r\nvar context = { "name" : "Ritesh Kumar", "occupation" : "developer" };\r\nfunction getTemplate(){\r\n	\r\n	 \r\n	 $.ajax("monkey.template.html", {\r\n      success: function(data) {\r\n         //$(''#main'').html($(data).find(''#main *''));\r\n         //$(''#notification-bar'').text(''The page has been successfully loaded'');\r\n         console.log(''yeah'',data);\r\n         TEMPLATE_OBJ = data;\r\n         makeIt("monkey");\r\n\r\n      },\r\n      error: function() {\r\n         //$(''#notification-bar'').text(''An error occurred'');\r\n         alert(''oh ouch!'');\r\n      }\r\n   });\r\n	\r\n}\r\ngetTemplate();\r\nfunction makeIt(id){\r\n	console.log(''make it right?'');\r\n	// Compile the template data into a function\r\n	var templateScript = Handlebars.compile(TEMPLATE_OBJ);\r\n	var html = templateScript(context);\r\n	// Insert the HTML code into the page\r\n	$(''#''+id).append(html);\r\n}\r\n \r\n</script>\r\n\r\n</body>\r\n</html>\r\n\r\n<!-- TEMPLATE FILE LOOKS LIKE THIS  --->\r\n\r\n   <div>\r\n      My name is {{name}}. I am a {{occupation}}.\r\n   </div>\r\n// just that div.', 'handlebars example mustache template', 'javascript'),
