(745, '<!Doctype html>\r\n<html>\r\n<head>\r\n<title>State</title>\r\n</head>\r\n<body>\r\n<button onclick="run();">run</button>\r\n<script>\r\nvar TrafficLight = function () {\r\n    var count = 0;\r\n    var currentState = new Red(this);\r\n \r\n    this.change = function (state) {\r\n        // limits number of changes\r\n        if (count++ >= 10) return;\r\n        currentState = state;\r\n        currentState.go();\r\n    };\r\n \r\n    this.start = function () {\r\n        currentState.go();\r\n    };\r\n}\r\n \r\nvar Red = function (light) {\r\n    this.light = light;\r\n \r\n    this.go = function () {\r\n        log.add("Red --> for 1 minute");\r\n        light.change(new Green(light));\r\n    }\r\n};\r\n \r\nvar Yellow = function (light) {\r\n    this.light = light;\r\n \r\n    this.go = function () {\r\n        log.add("Yellow --> for 10 seconds");\r\n        light.change(new Red(light));\r\n    }\r\n};\r\n \r\nvar Green = function (light) {\r\n    this.light = light;\r\n \r\n    this.go = function () {\r\n        log.add("Green --> for 1 minute");\r\n        light.change(new Yellow(light));\r\n    }\r\n};\r\n \r\n// log helper\r\n \r\nvar log = (function () {\r\n    var log = "";\r\n \r\n    return {\r\n        add: function (msg) { log += msg + "\\n"; },\r\n       // show: function () { alert(log); log = ""; }\r\n        show: function () { console.log(log); log = ""; }\r\n    }\r\n})();\r\n \r\nfunction run() {\r\n    var light = new TrafficLight();\r\n    light.start();\r\n \r\n    log.show();\r\n}\r\n \r\n//DESIGN PATTERN: STATE\r\n/*\r\n	function Group(context) {\r\n		this.context = context;\r\n	}\r\n	Group.prototype.context = '''';\r\n	Group.prototype.changeTheStateInContext = function(context) {\r\n		alert("changeTheStateInContext"+this.context);\r\n		this.context = context;\r\n	};\r\n	function GroupContext() {\r\n		\r\n		this.group = new DefaultState();\r\n	}\r\n	GroupContext.prototype.group = '''';\r\n	GroupContext.prototype.changeState = function() {\r\n		this.group.changeTheStateInContext(this);\r\n	};\r\n	GroupContext.prototype.setState = function(group) {\r\n		this.group = group;\r\n	};\r\n	function DefaultState() {\r\n		this.context = context;\r\n		Group.call(this);\r\n	}\r\n	DefaultState.prototype = new Group();\r\n	DefaultState.prototype.constructor = DefaultState;\r\n	DefaultState.prototype.changeTheStateInContext = function(context) {\r\n		alert("set state DefaultState");\r\n		this.context = context;\r\n		this.context.setState(new DefaultState());\r\n	}\r\n*/\r\n</script>\r\n</body>\r\n</html>', 'design pattern STATE with traffic light example', 'javascript'),
