(754, ' \r\n<!DOCTYPE html>\r\n<html>\r\n	<head>\r\n		<meta charset=''utf-8''>\r\n		<!--<link rel=''stylesheet'' type=''text/css'' href=''style.css''>\r\n		<script src=''script.js''></script> -->\r\n		<title>HTML DOC</title>\r\n	</head>\r\n	<body>\r\n	<h1>\r\n	Seed Inventory Technique:\r\n	</h1>\r\n	<p>Steps</p>\r\n	<ul>\r\n<li>1.) weigh the container and all seed packs</li>\r\n	<ul><li>TOTAL_WEIGHT</li></ul>\r\n<li>2.) weigh the container (empty) </li>\r\n	<ul><li>CONTAINER_WEIGHT</li></ul>\r\n<li>3.) weigh a set number of seed packs </li>\r\n	<ul><li>SET_COUNT(10)</li><li>SET_WEIGHT</li></ul>\r\n</ul>\r\n\r\n<p>Calculation Algorithm</p>\r\n<ul>\r\n<li>TOTAL_WEIGHT (minus) CONTAINER_WEIGHT = TOTAL_PACKAGES_WEIGHT</li>\r\n<li>TOTAL_PACKAGES_WEIGHT (divided by) SET_WEIGHT = NUMBER_OF_SETS</li>\r\n<li>NUMBER_OF_SETS (times) SET_COUNT = INVENTORY_COUNT</li>\r\n</ul>\r\n<p>example</p>\r\n<table border=''1'' width=''98%''>\r\n			 \r\n			 \r\n			<tr>\r\n					<td>TOTAL_WEIGHT</td>\r\n					<td><input id=''total_weight'' name=''total_weight'' value='''' onchange=''recalculate();'' placeholder=''total weight''/>oz\r\n					</td>\r\n			</tr>\r\n			 <tr>\r\n					<td>CONTAINER_WEIGHT</td>\r\n					<td><input id=''container_weight'' name=''container_weight'' value='''' onchange=''recalculate();''  placeholder=''container weight''/>oz\r\n					</td>\r\n			</tr>\r\n			<tr>\r\n				<td>SET_COUNT</td>\r\n				<td><input id=''set_count'' name=''set_count'' value='''' onchange=''recalculate();''  placeholder=''set count''/>#\r\n					</td>\r\n			</tr>\r\n			<tr>\r\n				<td>SET_WEIGHT</td>\r\n				<td><input id=''set_weight'' name=''set_weight'' value=''''  onchange=''recalculate();'' placeholder=''set weight''/>\r\n					oz</td>\r\n			</tr>\r\n			<tr>\r\n				<td>INVENTORY COUNT</td>\r\n				<td><input id=''inventory_count'' name=''inventory_count'' value='''' DISABLED placeholder=''inventory count''/>#\r\n					</td>\r\n			</tr>\r\n\r\n		</table>\r\n\r\n \r\n		<script>\r\n		(function(){\r\n		    console.log(''modular design pattern'');\r\n\r\n\r\n\r\n\r\n\r\n		}());\r\n\r\n		var total_weight = null;\r\n		var container_weight = null;\r\n		var set_count = null;\r\n		var set_weight = null;\r\n\r\n		var total_packages_weight = null;\r\n		var number_of_sets = null;\r\n\r\n		function recalculate(){\r\n			console.log(document.getElementById(''total_weight'').value);\r\n			if(\r\n				document.getElementById(''total_weight'').value != "" && \r\n				document.getElementById(''container_weight'').value != "" && \r\n				document.getElementById(''set_count'').value != "" && \r\n				document.getElementById(''set_weight'').value != "" \r\n\r\n				)\r\n			{\r\n 				total_weight = document.getElementById(''total_weight'').value;\r\n 				container_weight = document.getElementById(''container_weight'').value;\r\n 				set_count = document.getElementById(''set_count'').value;\r\n 				set_weight = document.getElementById(''set_weight'').value;\r\n\r\n 					total_packages_weight = parseFloat(total_weight).toFixed(2) - parseFloat(container_weight).toFixed(2);\r\n 					number_of_sets = parseFloat(total_packages_weight).toFixed(2)/parseFloat(set_weight).toFixed(2);\r\n 					inventory_count = parseFloat(number_of_sets).toFixed(2) * parseFloat(set_count).toFixed(2); \r\n 					document.getElementById(''inventory_count'').value = inventory_count;\r\n\r\n			}else{\r\n				console.log(''missing some data..'');\r\n			}\r\n		}\r\n		</script>\r\n	</body>\r\n</html>', 'physical inventory calc for parkseed calculator', 'javascript'),
