(782, '\r\nfunction projectWrapUp(obj) {\r\n             document.getElementById(''modal-projects'').click();\r\n            var html = "<div class=''ui_wrapper''> ";\r\n            html += ''<h2>Projects Wrap Up</h2>'';\r\n            var date = new Date();\r\n            console.log(date);\r\n            html += ''<p>'' + date + ''</p>'';\r\n            html += ''<div class="outline_wrapper">  '';\r\n             for (var i in obj) {\r\n              	 \r\n             	 if(obj[i][0] != null){\r\n             	 	// P R O J E C T    L E V E L \r\n             	 	 html += "<ul><div class=''project'' >Project:</div>";\r\n                		html += "<li style=''font-weight:bold;'' >" + obj[i][0].name + "</li>";\r\n                		html += "<li>" + obj[i][0].description + "</li>";\r\n                		html += "<li>owner: " + obj[i][0].owner + "</li>";\r\n                		html += "<li>" + obj[i][0].percent_complete + "%</li>";\r\n              	 	var obj_req = obj[i][1];\r\n             	 	 for(var j in obj_req){\r\n             	 	 	if(obj_req[j] != null){\r\n             	 	 		// R E Q U I R E M E N T     L E V E L \r\n             	 	 		html += "<ul><div class=''requirement''>Requirement:</div>";\r\n		                	html += "<li style=''font-weight:bold;'' >" + obj_req[j].name + "</li>";\r\n		                	html += "<li>" + obj_req[j].description + "</li>";\r\n		                	html += "<li>owner: " + obj_req[j].owner + "</li>";\r\n		                	html += "<li>" + obj_req[j].percent_complete + "%</li>";\r\n		                	\r\n	             	 	 	var obj_task = obj_req[j][0];\r\n	             	 	 	 for(var k in obj_task){\r\n		             	 	 	if(obj_task[k] != null){\r\n		             	 	 		// T A S K      L E V E L \r\n		             	 	 		html += "<ul><div class=''task''>Task:</div>";\r\n				                	html += "<li style=''font-weight:bold;'' >" + obj_task[k].name + "</li>";\r\n				                	html += "<li>" + obj_task[k].description + "</li>";\r\n				                	html += "<li>owner: " + obj_task[k].owner + "</li>";\r\n			             	 	 	html += "<li>" + obj_task[k].percent_complete + "%</li>";\r\n 			             	 	 	html += "</ul>";// end of task\r\n		             	 	 	}\r\n		             	 	 }\r\n		             	 	\r\n		             	 	  html += "</ul>";//end of requirements\r\n	             	 	 }\r\n	             	 	 \r\n             	 	 }\r\n             	 	\r\n             	 	 html += "</ul>";//end of project\r\n             	 } \r\n             	\r\n             }\r\n             html += "</div>";\r\n             html += "</div>";\r\n            $(''.modal-body'').html(html);\r\n \r\n }', 'wrap up mutidimensional project requirements task multi-relational', 'javascript'),
(783, '<!-- CSS Styles -->\r\n<style>\r\n  .speech {border: 1px solid #DDD; width: 300px; padding: 0; margin: 0}\r\n  .speech input {border: 0; width: 240px; display: inline-block; height: 30px;}\r\n  .speech img {float: right; width: 40px }\r\n</style>\r\n\r\n<!-- Search Form -->\r\n<form id="labnol" method="get" action="xxxhttps://www.google.com/search">\r\n  <div class="speech">\r\n    <input type="text" name="q" id="transcript" placeholder="Speak" />\r\n    <img onclick="startDictation()" src="//i.imgur.com/cHidSVu.gif" />\r\n  </div>\r\n</form>\r\n\r\n<!-- HTML5 Speech Recognition API -->\r\n<script>\r\n  function startDictation() {\r\n\r\n    if (window.hasOwnProperty(''webkitSpeechRecognition'')) {\r\n\r\n      var recognition = new webkitSpeechRecognition();\r\n\r\n      recognition.continuous = false;\r\n      recognition.interimResults = false;\r\n\r\n      recognition.lang = "en-US";\r\n      recognition.start();\r\n\r\n      recognition.onresult = function(e) {\r\n        document.getElementById(''transcript'').value\r\n                                 = e.results[0][0].transcript;\r\n        recognition.stop();\r\n        document.getElementById(''labnol'').submit();\r\n      };\r\n\r\n      recognition.onerror = function(e) {\r\n        recognition.stop();\r\n      }\r\n\r\n    }\r\n  }\r\n</script>', 'speech recognition', 'javascript'),
(784, '<!-- CSS Styles -->\r\n<style>\r\n  .speech {border: 1px solid #DDD; width: 300px; padding: 0; margin: 0}\r\n  .speech input {border: 0; width: 240px; display: inline-block; height: 30px;}\r\n  .speech img {float: right; width: 40px }\r\n</style>\r\n\r\n<!-- Search Form -->\r\n<form id="labnol" method="get" action="xxxhttps://www.google.com/search">\r\n  <div class="speech">\r\n    <input type="text" name="q" id="transcript" placeholder="Speak" />\r\n    <img onclick="startDictation()" src="//i.imgur.com/cHidSVu.gif" />\r\n  </div>\r\n</form>\r\n\r\n<!-- HTML5 Speech Recognition API -->\r\n<script>\r\n  function startDictation() {\r\n\r\n    if (window.hasOwnProperty(''webkitSpeechRecognition'')) {\r\n\r\n      var recognition = new webkitSpeechRecognition();\r\n\r\n      recognition.continuous = false;\r\n      recognition.interimResults = false;\r\n\r\n      recognition.lang = "en-US";\r\n      recognition.start();\r\n\r\n      recognition.onresult = function(e) {\r\n        document.getElementById(''transcript'').value\r\n                                 = e.results[0][0].transcript;\r\n        recognition.stop();\r\n        document.getElementById(''labnol'').submit();\r\n      };\r\n\r\n      recognition.onerror = function(e) {\r\n        recognition.stop();\r\n      }\r\n\r\n    }\r\n  }\r\n</script>', 'speech recognition', 'javascript'),
(785, '\r\n  <style>\r\ndiv.ui-datepicker{\r\n font-size:44px;\r\n}\r\n  .ui-datepicker td a:after\r\n{\r\n    content: "";\r\n    display: block;\r\n    text-align: center;\r\n    color: Blue;\r\n    font-size: small;\r\n    font-weight: bold;\r\n}  \r\n</style>\r\n<script>\r\n\r\n$(''#datepicker'').datepicker({\r\n    changeMonth: true,\r\n    changeYear: true,\r\n    minDate: 0,\r\n    //The calendar is recreated OnSelect for inline calendar\r\n    onSelect: function (date, dp) {\r\n        updateDatePickerCells();\r\n    },\r\n    onChangeMonthYear: function(month, year, dp) {\r\n        updateDatePickerCells();\r\n    },\r\n    beforeShow: function(elem, dp) { //This is for non-inline datepicker\r\n        updateDatePickerCells();\r\n    }\r\n});\r\nupdateDatePickerCells();\r\nfunction updateDatePickerCells(dp) {\r\n    /* Wait until current callstack is finished so the datepicker\r\n       is fully rendered before attempting to modify contents */\r\n    setTimeout(function () {\r\n        //Fill this with the data you want to insert (I use and AJAX request).  Key is day of month\r\n        //NOTE* watch out for CSS special characters in the value\r\n        var cellContents = {1: ''20'', 15: ''60'', 28: ''100''};\r\n\r\n        //Select disabled days (span) for proper indexing but apply the rule only to enabled days(a)\r\n        $(''.ui-datepicker td > *'').each(function (idx, elem) {\r\n            var value = cellContents[idx + 1] || 0;\r\n\r\n            /* dynamically create a css rule to add the contents with the :after                         \r\n               selector so we don''t break the datepicker functionality */\r\n            var className = ''datepicker-content-'' + value;\r\n\r\n            if(value == 0)\r\n                addCSSRule(''.ui-datepicker td a.'' + className + '':after {content: "\\\\a0";}''); //&nbsp;\r\n            else\r\n                addCSSRule(''.ui-datepicker td a.'' + className + '':after {content: "'' + value + ''";}'');\r\n\r\n            $(this).addClass(className);\r\n        });\r\n    }, 0);\r\n}\r\n\r\nvar dynamicCSSRules = [];\r\nfunction addCSSRule(rule) {\r\n    if ($.inArray(rule, dynamicCSSRules) == -1) {\r\n        $(''head'').append(''<style>'' + rule + ''</style>'');\r\n        dynamicCSSRules.push(rule);\r\n    }\r\n}\r\n\r\n\r\n</script>\r\n', 'datepicker and jquery and advanced size add text', 'javascript'),
(786, 'function timeDifference(date1,date2) {\r\n        var difference = date1.getTime() - date2.getTime();\r\n\r\n        var daysDifference = Math.floor(difference/1000/60/60/24);\r\n        difference -= daysDifference*1000*60*60*24\r\n\r\n       var hoursDifference = Math.floor(difference/1000/60/60);\r\n        difference -= hoursDifference*1000*60*60\r\n\r\n        var minutesDifference = Math.floor(difference/1000/60);\r\n        difference -= minutesDifference*1000*60\r\n\r\n        var secondsDifference = Math.floor(difference/1000);\r\n\r\n     console.log(''difference = '' + daysDifference + '' day/s '' + hoursDifference + '' hour/s '' + minutesDifference + '' minute/s '' + secondsDifference + '' second/s '');\r\n\r\n}', 'time difference (not verified yet)', 'javascript'),
(787, 'function chunkify_Array(array,chunk_size){\r\n	//CHUNKIFY: split an array into chunks of a certain length \r\n	var chunked = [];\r\n    var i,j,temparray,chunk = chunk_size;\r\n	for (i=0,j=array.length; i<j; i+=chunk) {\r\n	    temparray = array.slice(i,i+chunk);\r\n	    chunked.push(temparray);\r\n	}\r\n	return chunked;\r\n}', 'CHUNKIFY: split an array into chunks of a certain length ', 'javascript'),
