(806, '<div id="content_stage" class="row plugin theme-dark">\r\n    <!--  ///////////    THE MODAL   /////////////// -->\r\n    <div style=''margin-top:5%;'' class=''modal fade theme-dark'' id=''modal-container-plugin'' role=''dialog'' aria-labelledby=''myModalLabel'' aria-hidden=''false''>\r\n        <div class=''modal-dialog''>\r\n            <div class=''modal-content''>\r\n                <div class=''modal-header''>\r\n                    <button type=''button'' class=''close'' data-dismiss=''modal'' aria-hidden=''false''> Ã— </button>\r\n                    <h4 class=''modal-title'' id=''myModalLabel''> Edit   </h4>\r\n                </div>\r\n                <div class=''modal-body''>\r\n                    ...\r\n                </div>\r\n                <div class=''modal-footer''>\r\n                    <button type=''button'' class=''btn btn-default'' data-dismiss=''modal''>\r\n                        Close\r\n                    </button>\r\n                </div>\r\n            </div>\r\n        </div>\r\n    </div>\r\n    <a id=''modal-plugin'' style='''' href=''#modal-container-plugin'' role=''button'' class=''btn invisible'' data-toggle=''modal''>modal-1</a>\r\n\r\n    <div class="col-lg-12 col-md-12 col-sm-12 col-xs-12>">\r\n        <!-- CALC -->\r\n        <script>\r\n            var paycheck = null;\r\n            var expense = [];\r\n            var duedate = null;\r\n            var company = null;\r\n            var subtotal = null;\r\n            var remainder = null;\r\n            var total = null;\r\n        </script>\r\n        <style>\r\n            .plugin-bill-calc {\r\n                background-color: #000;\r\n                border: solid 1px #666;\r\n                border-radius: 3px;\r\n                color: orange;\r\n            }\r\n            .plugin-bill-calc input,\r\n            .plugin-bill-calc select {\r\n                color: green;\r\n                background-color: #111;\r\n                border: solid 1px #66