segment-lbl col-lg-12 col-md-12 col-sm-12 col-xs-12">Add Expense </div>\r\n                    <div class="col-lg-12 col-md-12 col-sm-12 col-xs-12">\r\n\r\n\r\n                        <div class=''row''>\r\n                            <div class="lbl col-lg-12 col-md-12 col-sm-12 col-xs-12">Expense</div>\r\n                            <div class="col-lg-12 col-md-12 col-sm-12 col-xs-12">\r\n                                <select id=''company'' onchange=''checkForOther();''>\r\n                                    <option class=''theme-dark''>Automotive</option>\r\n                                    <option class=''theme-dark''>Cable</option>\r\n                                    <option class=''theme-dark''>Credit Card Payment</option>\r\n                                    <option class=''theme-dark''>Electric</option>\r\n                                    <option class=''theme-dark''>Entertainment</option>\r\n                                    <option class=''theme-dark''>Fuel</option>\r\n                                    <option class=''theme-dark''>Groceries</option>\r\n                                    <option class=''theme-dark''>Healthcare</option>\r\n                                    <option class=''theme-dark''>Household</option>\r\n                                    <option class=''theme-dark''>Internet</option>\r\n                                    <option class=''theme-dark''>Loan Payment</option>\r\n                                    <option class=''theme-dark''>Phone</option>\r\n                                    <option class=''theme-dark''>School</option>\r\n                                    <option class=''theme-dark''>Transportation</option>\r\n                                    <option class=''theme-dark''>Utility</option>\r\n                                    <option class=''theme-dark''>Water</option>\r\n                                    <option class=''theme-dark''>Miscellaneous</option>\r\n\r\n                                </select>\r\n                                <input id=''other_company'' placeholder=''other bill'' disabled style=''display:none;'' />\r\n                            </div>\r\n                        </div>\r\n                        <div class=''row''>\r\n                            <div class="lbl col-lg-12 col-md-12 col-sm-12 col-xs-12">Amount</div>\r\n                            <div class="col-lg-12 col-md-12 col-sm-12 col-xs-12">\r\n                                $&nbsp;\r\n                                <input id=''expense'' type=''number'' placeholder=''expense'' input=''number'' value=''0'' />\r\n                            </div>\r\n                        </div>\r\n                        <div class=''row''>\r\n                            <div class="lbl col-lg-12 col-md-12 col-sm-12 col-xs-12">Due</div>\r\n                            <div class="col-lg-12 col-md-12 col-sm-12 col-xs-12">\r\n                                <input id=''duedate'' placeholder=''duedate'' type=''date'' />\r\n                            </div>\r\n                        </div>\r\n                        <div class=''row''>\r\n                            <div class="col-lg-12 col-md-12 col-sm-12 col-xs-12">\r\n                                <button onclick=''addExpense()''>\r\n                                    Add to Bills\r\n                                </button>\r\n                            </div>\r\n                        </div>\r\n\r\n                    </div>\r\n                </div>\r\n\r\n            </div>\r\n            <div class=''segment  col-lg-6 col-md-6 col-sm-12 col-xs-12 text-left''>\r\n                <div class=''row''>\r\n                    <div class="segment-lbl col-lg-12 col-md-12 col-sm-12 col-xs-12" id=''expenses''>Expenses</div>\r\n                </div>\r\n                <div class=''row''>\r\n                    <div class="lbl col-lg-4 col-md-4 col-sm-4 col-xs-4">Expense</div>\r\n                    <div class="lbl col-lg-4 col-md-4