/\r\n\r\n                    var data = $("form").serialize();\r\n                    console.log(data);\r\n\r\n                    document.getElementById(''mailto_anchor'').setAttribute(''href'', ''mailto:'' + mailto + ''?subject=paycheckTopaycheck&message='' + data + '''');\r\n                    // = mailto;\r\n                    console.log(mailto);\r\n                    alert(''check the console.'');\r\n\r\n                    console.log(typeof data);\r\n                    var dataString = JSON.stringify(data);\r\n                    console.log(dataString);\r\n\r\n                    alert(''see data in console...'');\r\n\r\n                }\r\n\r\n                function setPaycheck() {\r\n                    paycheck = document.getElementById(''paycheck'').value;\r\n                }\r\n\r\n                function addExpense() {\r\n                    if (parseFloat(paycheck) > 0) {\r\n                        var exp = document.getElementById(''expense'').value;\r\n                        var duedate = document.getElementById(''duedate'').value;\r\n                        var company = document.getElementById(''company'').value;\r\n\r\n                        if (document.getElementById(''other_company'').style.display == ''block'') {\r\n                            company = document.getElementById(''other_company'').value;\r\n                            document.getElementById(''other_company'').style.display = ''none'';\r\n                        }\r\n\r\n                        var obj = {};\r\n                        obj.expense = exp;\r\n                        obj.duedate = duedate;\r\n                        obj.company = company;\r\n\r\n                        expense.push(obj);\r\n                        clearExpense();\r\n                        calculateOut();\r\n                    } else {\r\n                        alert(''Put in your Paycheck Amount 1st.'');\r\n                        clearExpense();\r\n                    }\r\n                }\r\n\r\n                function clearExpense() {\r\n                    document.getElementById(''expense'').value = ''0'';\r\n                    document.getElementById(''duedate'').value = '''';\r\n                    /*document.getElementById(''company'').value = '''';*/\r\n\r\n                }\r\n\r\n                function calculateOut() {\r\n                    var expense_html = "<form id=''form'' >";\r\n                    for (var i = 0; i < expense.length; i++) {\r\n                        console.log(expense[i]);\r\n                        subtotal = subtotal + parseFloat(expense[i][''expense'']);\r\n                        expense_html += "<div class=''col-lg-4 col-md-4 col-sm-4 col-xs-4''><input type=''text'' name=''company'' disabled value=''" + expense[i][''company''] + "''/></div><div class=''col-lg-4 col-md-4 col-sm-4 col-xs-4''><input type=''text'' disabled name=''expense'' value=''$" + expense[i][''expense''] + "''/></div><div class=''col-lg-4 col-md-4 col-sm-4 col-xs-4''><input type=''text'' disabled name=''due'' value=''" + expense[i][''duedate''] + "''/></div>";\r\n                    }\r\n                    expense_html += "</form>";\r\n                    document.getElementById(''expense_table_body'').innerHTML = expense_html;\r\n                    remainder = paycheck - subtotal;\r\n                    document.getElementById(''out'').value = subtotal;\r\n                    total = subtotal;\r\n                    document.getElementById(''remainder'').value = remainder;\r\n                    if (remainder < 0) {\r\n                        document.getElementById(''remainder'').style = "color:red;";\r\n                    } else {\r\n                        document.getElementById(''remainder'').style = "color:green;";\r\n                    }\r\n                    subtotal = 0;\r\n                }\r\n\r\n\r\n\r\n\r\n                function summary() {\r\n\r\n                    var table_header = ''summary'';\r\n                    $(''#modal-plugin'').click();\r\n\r\n                    var html = " <div class=''row''>";\r\n                    for (var e in expense) {\r\n                        html += "<div class=''col-lg-4 col-md-4 col-sm-4 col-xs-4'' >" + expense[e].company + "</div>";\r\n                        html += "<div class=''col-lg-4 col-md-4 col-sm-4 col-xs-4'' >" + expense[e].expense + "</div>";\r\n                        html += "<div class=''col-lg-4 col-md-4 col-sm-4 col-xs-4'' >" + expense[e].duedate + "</div>";\r\n                        html += "";\r\n                    }\r\n                    html += "<div class='''' >total:" + total + "</div>";\r\n                    html += "<div class='''' >remainder:" + remainder + "</div>";\r\n                    html += "</div>";\r\n                    $(''.modal-body'').html(html);\r\n\r\n                    //LEFT OFF HERE :\r\n                    // ajax submit a json object to a snapshot table with id,name,and description.\r\n\r\n                }\r\n\r\n\r\n                function checkForOther() {\r\n                    if (document.getElementById(''company'').value == ''other'') {\r\n                        //other_company\r\n                        //document.getElementById(''other_company'').style(''display'')=''block'';\r\n                        //document.getElementById("other_company").style.opacity = "1";\r\n                        //document.getElementById(''mailto_anchor'').setAttribute(''href'',''val'');\r\n                        //document.getElementById(''other_company'').setAttribute(''enabled'');\r\n                        //alert(''foo'');\r\n                        document.getElementById(''other_company'').disabled = false;\r\n                        document.getElementById(''other_company'').style.display = ''block'';\r\n                        //document.getElementById(''other_company'').setAttribute(''placeholder'',''what other co?'');\r\n\r\n                    }\r\n                }(function() {\r\n                    console.log(''modular design pattern'');\r\n                }());\r\n            </script>\r\n        </div>\r\n        <!-- end -->\r\n    </div>\r\n</div>', 'app-lite wup plugin budget calculator', 'php'),
(807, 'Disable all scrolling:\r\n\r\n//create the exception variable.\r\nvar Scrollable = ''.scrollable'';\r\n\r\n    //prevent all scrolling.\r\n    $(document).on(''touchmove'', function(e) {\r\n        e.preventDefault();\r\n    });\r\n\r\n    $(''body'').on(''touchstart'', Scrollable, function(e) {\r\n        if (e.currentTarget.scrollTop === 0) {\r\n            e.currentTarget.scrollTop = 1;\r\n        } else if (e.currentTarget.scrollHeight === e.currentTarget.scrollTop + e.currentTarget.offsetHeight) {\r\n            e.currentTarget.scrollTop -= 1;\r\n        }\r\n    });\r\nThe following is to allow scrolling on the exception:\r\n\r\n    // Stops preventDefault from being called on document if it sees a scrollable div\r\n    $(''body'').on(''touchmove'', Scrollable, function(e) {\r\n        e.stopPropagation();\r\n    });\r\nWhat you want to do, is set a class called scrollable on every element that you want to be scrollable. You can of course always call the stopPropagation() when you want.\r\n\r\nThis way you can easily decide if you want something to be scrollable or not, overflow is not always working as you think it would.', 'bootstrap nav bar scrolling issue and background scrolling', 'javascript'),
