(809, '<!DOCTYPE html>\r\n<html >\r\n<head>\r\n<title>graph</title>\r\n<style>\r\nhtml{\r\n\r\n}\r\nbody{\r\n	padding:20px !important;\r\n}\r\n\r\n.demo-container {\r\n	box-sizing: border-box;\r\n	width: 425px;\r\n	height: 225px;\r\n	padding: 20px 15px 15px 15px;\r\n	margin: 15px auto 30px auto;\r\n	border: 1px solid #ddd;\r\n	background: #fff;\r\n	/*background: linear-gradient(#f6f6f6 0, #fff 50px);\r\n	background: -o-linear-gradient(#f6f6f6 0, #fff 50px);\r\n	background: -ms-linear-gradient(#f6f6f6 0, #fff 50px);\r\n	background: -moz-linear-gradient(#f6f6f6 0, #fff 50px);\r\n	background: -webkit-linear-gradient(#f6f6f6 0, #fff 50px);*/\r\n	box-shadow: 0 3px 10px rgba(0,0,0,0.15);\r\n	-o-box-shadow: 0 3px 10px rgba(0,0,0,0.1);\r\n	-ms-box-shadow: 0 3px 10px rgba(0,0,0,0.1);\r\n	-moz-box-shadow: 0 3px 10px rgba(0,0,0,0.1);\r\n	-webkit-box-shadow: 0 3px 10px rgba(0,0,0,0.1);\r\n}\r\n\r\n.demo-placeholder {\r\n	width: 100%;\r\n	height: 100%;\r\n	font-size: 14px;\r\n	line-height: 1.2em;\r\n}\r\n\r\n</style>\r\n<!-- <link href="examples.css" rel="stylesheet" type="text/css"> -->\r\n	<!--[if lte IE 8]><script language="javascript" type="text/javascript" src="../../excanvas.min.js"></script><![endif]-->\r\n	<script language="javascript" type="text/javascript" src="../jquery.js"></script>\r\n	<script language="javascript" type="text/javascript" src="../jquery.flot.js"></script>\r\n</head>\r\n<body>\r\n<h1>Plan</h1>\r\n<p><input id="date" type=''text'' name=''date'' placeholder=''date''/></p>\r\n<p><input id="amt" type=''text'' name=''amt'' placeholder=''amt''/></p>\r\n<p><button onclick=''calc()''>go</button>\r\n<div class="demo-container">\r\n	<div id="placeholder" class="demo-placeholder"></div>\r\n</div>\r\n<script>\r\nvar arrayCoords = [];\r\nvar data = [];\r\nvar graph = null;\r\nvar alreadyFetched = {};\r\nvar options = {\r\n			lines: {\r\n				show: true\r\n			},\r\n			points: {\r\n				show: true\r\n			},\r\n			xaxis: {\r\n				tickDecimals: 0,\r\n				tickSize: 1\r\n			}\r\n		};\r\nfunction calc(){\r\n	var date_obj = document.getElementById(''date'');\r\n	var amt_obj = document.getElementById(''amt'');\r\n	var tempCoords = [];\r\n	tempCoords = [parseInt(date_obj.value),parseInt(amt_obj.value)];\r\n	arrayCoords.push(tempCoords);\r\n	date_obj.value = "";\r\n	amt_obj.value="";\r\n	var series = {};\r\n	series.label = "Graph(amt by date)";\r\n	series.data = arrayCoords;\r\n	var firstcoordinate = "(" + series.data[0][0] + ", " + series.data[0][1] + ")";\r\n	// Push the new data onto our existing data array\r\n	if (!alreadyFetched[series.label]) {\r\n		alreadyFetched[series.label] = true;\r\n		data.push(series);\r\n	}\r\n	$.plot("#placeholder", data, options);\r\n}\r\n</script>\r\n \r\n</body>\r\n</html>', 'flot graph amount by date via simple html inputs', 'javascript'),
