(827, '<!DOCTYPE html>\r\n<head>\r\n  <script src="../lib/js/jquery-1.11.2.min.js"></script>\r\n<style>\r\n/*  -------   elementalCSS   ----------- */     \r\n.form_container{\r\n    width:90%;\r\n    margin:auto;\r\n    border:solid 1px #ccc;\r\n\r\n}\r\n.col-lg-12{\r\n    width:100% !important;\r\n    min-width:100% !important;\r\n}\r\n#cumulating_data a{\r\n    text-decoration:none !important;\r\n}\r\n \r\n/*input,button,a{padding:2px;margin:5%;width:90%;}*/\r\n</style>\r\n\r\n<script>\r\nfunction addEachRecipientAsNewOrderIntoCart(obj){\r\n        console.log(obj);\r\n        var URL = "http://localhost:8989/jppa/Personalization/elemental_endpoint.php"; // endpoint = <?php  echo(json_encode($_POST)); ?>\r\n        setTimeout(function(){\r\n             $.post(URL,obj,\r\n                function(data, status){\r\n                     console.log(data);\r\n                    console.log(status);\r\n                });\r\n            \r\n    }, 1000);\r\n\r\n}\r\n\r\n\r\n\r\n\r\n  function totalSubmission(){\r\n      for (var i = 0; i < arrayOfRecipients.length; i++) {\r\n         addEachRecipientAsNewOrderIntoCart(arrayOfRecipients[i])\r\n     }\r\n    console.log(''a function'');\r\n     return false;\r\n }\r\n/////////// -----  elementalJS  -------- //////////////////////////////////////////////////////\r\n var arrayOfRecipients = [];\r\n\r\nvar element_count = 0;\r\nvar element, id, name, value, placeholder, classList, text, eventType, src, height, width, href, target, type, method, action = null;\r\neventType = "click";\r\nvar parent_id = "elemental";\r\n\r\nfunction createParentElementWithId(element, id, name, value, placeholder, classList, text, eventType, src, height, width, href, target, type, method, action) {\r\n    var element = document.createElement(element);\r\n    var t = document.createTextNode(text);\r\n    element.appendChild(t);\r\n    element.setAttribute("id", id);\r\n    document.getElementById("elemental").appendChild(element);\r\n    var element1 = document.getElementById(id);\r\n    if (element1) {\r\n        //element1.style.background = "#ff0";\r\n        //Classes\r\n        for (var c in classList) {\r\n            element1.classList.add(classList[c]);\r\n        }\r\n        //EventListeners\r\n        element1.addEventListener(eventType, function(event) {\r\n            var e = event;\r\n            var obj = this;\r\n            var data = [obj, e];\r\n            element_callback(data);\r\n        }, false);\r\n        //Attributes\r\n        element1.setAttribute(''name'', name);\r\n        element1.setAttribute(''value'', value);\r\n        element1.setAttribute(''placeholder'', placeholder);\r\n        element1.setAttribute(''src'', src);\r\n        element1.setAttribute(''width'', width);\r\n        element1.setAttribute(''height'', height);\r\n        element1.setAttribute(''href'', href);\r\n        element1.setAttribute(''target'', target);\r\n        element1.setAttribute(''type'', type);\r\n        element1.setAttribute(''method'', method);\r\n        element1.setAttribute(''action'', action);\r\n\r\n    }\r\n}\r\n\r\n\r\nfunction appendChildToParentId(parent_id, element, id, name, value, placeholder, classList, text, eventType, src, height, width, href, target, type, method, action) {\r\n    var element = document.createElement(element);\r\n    var t = document.createTextNode(text);\r\n    element.appendChild(t);\r\n    element.setAttribute("id", id);\r\n    document.getElementById(parent_id).appendChild(element);\r\n    var element1 = document.getElementById(id);\r\n    if (element1) {\r\n         //Classes\r\n        for (var c in classList) {\r\n            element1.classList.add(classList[c]);\r\n        }\r\n        //EventListeners\r\n        element1.addEventListener(eventType, function(event) {\r\n            var e = event;\r\n            var obj = this;\r\n            var data = [obj, e];\r\n            element_callback(data);\r\n        }, false);\r\n        //Attributes\r\n        element1.setAttribute(''name'', name);\r\n        element1.setAttribute(''value'', value);\r\n        element1.setAttribute(''placeholder'', placeholder);\r\n        element1.setAttribute(''src'', src);\r\n        element1.setAttribute(''width'', width);\r\n        element1.setAttribute(''height'', height);\r\n        element1.setAttribute(''href'', href);\r\n        element1.setAttribute(''target'', target);\r\n        element1.setAttribute(''type'', type);\r\n        element1.setAttribute(''method'', method);\r\n        element1.setAttribute(''action'', action);\r\n    }\r\n}\r\n\r\nfunction e_input(parent_id, element, name, value, text, eventType, classList, type) {\r\n    element_count++;\r\n    id = ''input'' + element_count;\r\n    classList = classList;\r\n    [''textBold'', ''fontCourier'', ''animated'', ''fadeIn''];\r\n    element = element;\r\n    text = text;\r\n    name = name;\r\n    value = value;\r\n    placeholder = text;\r\n    eventType = eventType;\r\n    parent_id = parent_id;\r\n    type = type;\r\n    appendChildToParentId(parent_id, element, id, name, value, placeholder, classList, text, eventType, src, height, width, href, target, type, method, action);\r\n\r\n}\r\n\r\n</script>\r\n\r\n</head>\r\n<body onload="e_elementalUI()">\r\n<div class="form_container">\r\n    <form id="elemental" name="myForm" onsubmit="return getFormData();"> \r\n    <!-- Dynamically insert inputs here. -->\r\n    </form>\r\n    <div id="cumulating_data">\r\n    <!-- Dynamically insert chosen recipients here. -->\r\n    </div>\r\n  \r\n     <form id="totalSubmission_id" name="totalSubmission_form" onsubmit="return totalSubmission();"> \r\n    <!-- SUBMIT FORMS VIA AJAX -->\r\n      <div class="col-lg-12"><button class="col-lg-12" type="submit" >Add All Recipients To Your Cart</button> </div>\r\n    </form>\r\n</div>  \r\n<script>\r\n function element_callback(data) {\r\n     console.log(''element callback'');\r\n     var obj = data[0];\r\n     var e = data[1];\r\n     console.log(obj.id + "- " + e.type);\r\n }\r\n\r\n function e_elementalUI() {\r\n     var form_id = "elemental";\r\n     console.log(''form_id'' + form_id);\r\n     e_input(form_id, "Input", "name", "", "enter name", "blur", [''col-lg-12'', ''data'']);\r\n     e_input(form_id, "Input", "email", "", "enter email", "blur", [''col-lg-12'', ''data'']);\r\n     e_input(form_id, "Button", "method", "submitForm", "add recipient", "click", [''col-lg-12'', ''data_submitter''], "submit"); //normally would be ''submit''\r\n }\r\n\r\n\r\n\r\n function getFormData(obj) {\r\n     var recipient = {};\r\n     var inputs = document.getElementsByClassName("data");\r\n     for (var i = 0; i < inputs.length; i++) {\r\n         if (i == 0) {\r\n             recipient.name = document.getElementById(inputs.item(i).id).value;\r\n         } else if (i == 1) {\r\n             recipient.email = document.getElementById(inputs.item(i).id).value;\r\n         }\r\n     }\r\n     recipient.id = ''recipient_'' + arrayOfRecipients.length;\r\n     arrayOfRecipients.push(recipient);\r\n     console.log(arrayOfRecipients);\r\n     displayCumulatingData(arrayOfRecipients)\r\n     return false;\r\n }\r\n\r\n function removeData(obj) {\r\n     console.log(''REMOVE'');\r\n     console.log(obj);\r\n     var parent = findUpId(obj);\r\n     var parent_id = parent.id;\r\n      var parent = document.getElementById(parent_id);\r\n        var child = document.getElementById(obj.id);\r\n      parent.removeChild(child);\r\n     ///////////////////////////////\r\n     for (var i = 0; i < arrayOfRecipients.length; i++) {\r\n\r\n         if (arrayOfRecipients[i].id == obj.id) {\r\n             arrayOfRecipients.splice(i, 1); //removes 1 element at position i \r\n             break;\r\n         }\r\n     }\r\n      obj.remove(); // from UI\r\n     console.log(''RESULTING ARRAY:'');\r\n     console.log(arrayOfRecipients);\r\n }\r\n\r\n function displayCumulatingData(arg) {\r\n     document.getElementById(''cumulating_data'').innerHTML = "";\r\n     console.log(typeof arg);\r\n     for (var i = 0, max = arg.length; i < max; i++) {\r\n         console.log(''i:'' + i);\r\n         console.log(arg[i].name);\r\n         console.log(arg[i].email);\r\n         var spacing = "&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;";\r\n         document.getElementById(''cumulating_data'').innerHTML += "<div  id=''li_" + arg[i].id + "''   ><a id=''" + arg[i].id + "'' href=''#'' onclick=''removeData(this);'' ><input type=''checkbox'' checked = checked />" +spacing+ arg[i].name + "</a></div>";\r\n     }\r\n     // clear input elements\r\n     document.getElementById(''input1'').value = "";\r\n     document.getElementById(''input2'').value = "";\r\n     document.getElementById(''input1'').focus();\r\n }\r\n\r\n function findUpId(el) {\r\n     console.log(''--- findUpId(el)...'');\r\n     while (el.parentNode) {\r\n         el = el.parentNode;\r\n         console.log(''--- looking...'');\r\n         if (el.id != "")\r\n             return el;\r\n     }\r\n     return null;\r\n }\r\n function submitChosenRecipients(){\r\n    //alert(''loop through array of chosen and submit the normal form once per recipient.'');\r\n    //\r\n      for (var i = 0; i < arrayOfRecipients.length; i++) {\r\n\r\n        /* if (arrayOfRecipients[i].id == obj.id) {\r\n            // specific\r\n             break;\r\n         }\r\n         */\r\n         //generally\r\n         console.log(arrayOfRecipients[i].id + "--" + arrayOfRecipients[i].name +"--"+ arrayOfRecipients[i].email)\r\n\r\n     }\r\n\r\n }\r\n\r\n\r\n</script>\r\n\r\n\r\n</body>\r\n</html>\r\n', ' dynamically build a list, handle form submission with ajax for array of items in the list.with elemental', 'javascript'),
(828, 'function validateAddToCart() {\r\n\r\n	// Convert non-numeric quantities and quantities less than 0 to an empty string.\r\n	$( "input:text[name^=''dmilist_Qty_'']" ).val(function(index, value) {\r\n		var i = parseInt(value);\r\n		return (!isNaN(i) && i > 0 ) ? i : "";\r\n	});\r\n	\r\n	// If all quantity field values are empty strings.\r\n	if($( "input:text[name^=''dmilist_Qty_''][value!='''']" ).length == 0) {\r\n		alert("Please enter a quantity for at least one item.");\r\n		$( "input:text[name^=''dmilist_Qty_'']" ).first().focus().select();\r\n		return false;\r\n	}\r\n	\r\n\r\n	// Iterate over lookup attribute select fields for all products.\r\n	var isValid = true;\r\n	$( "select[name^=''dmilist_DYNAMIC_Attribute_'']" ).each(function(index) {\r\n		var element = $(this);\r\n		if(element.val() == "") {\r\n\r\n			// If the corresponding quantity field is not empty, then validate.\r\n			if($( "input:text[name=''dmilist_Qty_" + element.attr( "name" ).split( "_" )[4] + "'']" ).val() != "") {\r\n				element.focus();\r\n				alert("Please select a " + element.parent().children( "label" ).html());\r\n				isValid = false;\r\n				return false;\r\n			}\r\n		}\r\n	});\r\n	\r\n	if(!isValid) {\r\n		return isValid;\r\n	};\r\n\r\n\r\n	// Validate if SPECIFIC e-mail address input attribute is added to product\r\n/*	var regexp = /^.+\\@(\\[?)[a-zA-Z0-9\\-\\.]+\\.([a-zA-Z]{2,4}|[0-9]{1,3})(\\]?)$/;\r\n	var element = $(this);\r\n	var emailaddress = $("input:text[name=''dmilist_DYNAMIC_Attribute_100000000136_" + element.attr( "name" ).split( "_" )[4] + "'' ]").val();\r\n*/	\r\n\r\n\r\n	$( "input:text[name^=''dmilist_DYNAMIC_Attribute_100000000136'']" ).each(function(index) {\r\n		var element = $(this);\r\n		if(element.val().length < 0) {\r\n			alert("Please enter an e-mail address");\r\n		}\r\n	});\r\n\r\n		\r\n	/*if(emailaddress.length == 0) {\r\n		alert("Please enter an e-mail address");  	\r\n		$( "input:text[name^=''dmilist_DYNAMIC_Attribute_100000000136'']" ).first().focus().select();\r\n		return false;\r\n	}\r\n	\r\n	if (!regexp.test(emailaddress)) {\r\n		alert("Please enter a valid e-mail address")\r\n	//	$( "input:text[name^=''dmilist_DYNAMIC_Attribute_100000000136'']" ).first().focus().select();\r\n		return false;\r\n	}	*/\r\n\r\n\r\n\r\n	// Iterate over input attribute text input fields for all products.\r\n	$( "input:text[name^=''dmilist_DYNAMIC_Attribute_'']" ).each(function(index) {\r\n		this.value = $.trim(this.value);\r\n	});\r\n	$( "input:text[name^=''dmilist_DYNAMIC_Attribute_'']" ).each(function(index) {\r\n		var element = $(this);\r\n		if(element.val().length < parseInt(element.attr("minlength"))) {\r\n\r\n			// If the corresponding quantity field is not empty, then validate.\r\n			if($( "input:text[name=''dmilist_Qty_" + element.attr( "name" ).split( "_" )[4] + "'']" ).val() != "") {\r\n				element.focus();\r\n				alert("Please enter a value in the " + element.parent().children( "label" ).html() + " field.");\r\n				isValid = false;\r\n				return false;\r\n			}\r\n		}\r\n	});\r\n	return isValid;\r\n}', 'form validation code from kalio', 'javascript'),
