Index ) == 1''>\r\n			<dmi:then>\r\n					<dmi:expression value=''SetInViewState<string>("element1", ((string)Container.DataItem).ToString())'' /><%-- (string) is type casting --%>\r\n		    		<dmi:expression value=''GetFromViewState<String>("element1")''></dmi:expression> \r\n			</dmi:then>\r\n		</dmi:if>\r\n	</dmi:loop>\r\n </dmi:loop>\r\n\r\n\r\n<%-- dmi Array Index  --%>\r\n<p>dmi Array Index</p>\r\n<dmi:if dmisource=''Container.DataItem'' expression=''(Container.ItemIndex ) == 0''>\r\n			<dmi:then>\r\n			</dmi:then>\r\n</dmi:if>\r\n\r\n<%-- dmi Condition IF THEN  --%>\r\n<p>dmi Condition IF THEN</p>\r\n <dmi:if dmisource=''Container.DataItem'' expression=''(Container.ItemIndex + 1) == 1''>\r\n		<dmi:then>\r\n			<%-- IF CODE HERE --%>\r\n		</dmi:then>\r\n		<dmi:else>\r\n			<%-- ELSE CODE HERE --%>\r\n		</dmi:else>\r\n</dmi:if>\r\n\r\n<%-- dmi SESSION VARS  --%>\r\n<p>dmi SESSION VARS</p>\r\n<dmi:expression value=''Core.SetInSession("SESSION_STRING", GetFromViewState<String>("myString"))''></dmi:expression>\r\n<p><dmi:expression value=''Core.GetFromSession<string>("SESSION_STRING")''></dmi:expression></p>\r\n\r\n<dmi:expression value=''Core.SetInSession("SESSION_INT", GetFromViewState<int>("myInt"))''></dmi:expression>\r\n<p><dmi:expression value=''Core.GetFromSession<int>("SESSION_INT")''></dmi:expression></p>\r\n\r\n<dmi:expression value=''Core.SetInSession("SESSION_BOOL", GetFromViewState<bool>("myBool"))''></dmi:expression>\r\n<p><dmi:expression value=''Core.GetFromSession<bool>("SESSION_BOOL")''></dmi:expression></p>\r\n     \r\n <%-- dmi Date Time  --%>\r\n<p> dmi Date Time</p>\r\n<dmi:expression value=''Core.GetDateTime("yyyy")''></dmi:expression>\r\n<dmi:expression value=''Core.GetDateTime("yyMMddHHmm")''></dmi:expression>\r\n\r\n<%-- dmi Regex.Replace --%>\r\n<p>dmi Regex.Replace </p>\r\n<dmi:Expression value=''SetInViewState<string>("myOtherStringForRegex", "123-555-6B")''></dmi:Expression>  	 	\r\n<p><dmi:expression value=''GetFromViewState<String>("myOt