(849, 'The import statement is used to import functions, objects or primitives that have been exported from an external module, another script, etc.\r\n\r\nNote: This feature is not implemented in any browsers natively at this time. It is implemented in many transpilers, such as the Traceur Compiler, Babel, Rollup or Webpack.\r\nimport defaultMember from "module-name";\r\nimport * as name from "module-name";\r\nimport { member } from "module-name";\r\nimport { member as alias } from "module-name";\r\nimport { member1 , member2 } from "module-name";\r\nimport { member1 , member2 as alias2 , [...] } from "module-name";\r\nimport defaultMember, { member [ , [...] ] } from "module-name";\r\nimport defaultMember, * as name from "module-name";\r\nimport "module-name";', 'import', 'javascript'),
(850, ' /*=====================================  * Function#: X   ======================================*/\r\n  if (fn_int == X) {  	\r\n	  //Declare variables 	\r\n	  StringBuilder sb = new StringBuilder(); 	 	\r\n	  sb.AppendLine("Function 2 Debug Begin "); 	\r\n	  sb.AppendLine(string.Format("productNumber:{0}", productNumber)); 	\r\n	  sb.AppendLine(string.Format("productQuantity_string:{0}", productQuantity_string));	 	 	\r\n	  try 	{		 		  		\r\n		  sb.AppendLine(string.Format("productNumber_array.Length:{0}", productNumber_array.Length)); 		\r\n		  //Loop over strings 		\r\n		  for (int i = 0; i < productNumber_array.Length; i++) { 		 			\r\n			  sb.AppendLine(string.Format("Inside Product Number Loop ", "")); 			\r\n			  sb.AppendLine(string.Format("productNumber_array[i]:{0}", productNumber_array[i]));  			\r\n			  //Get Requested Product 			\r\n			  Product[] validate_product = Core.GetProductByProductNumber(productNumber_array[i]);  			\r\n			  //Loop through single product object 			\r\n			  foreach(Product validProduct in validate_product) { 			 				\r\n			  sb.AppendLine(string.Format("Inside product loop, Product Number:{0}", validProduct.ProductNumber));  				\r\n			  try {  					\r\n			  string productNumber_single = productNumber_array[i];  					\r\n			  //Get Requested Product 					\r\n			  Product[] products = Core.GetProductByProductNumber(productNumber_single.Trim());  					  				\r\n			  }catch (Exception e) { 					 					 					\r\n				  sb.AppendLine(string.Format("Debug Message:{0}", e.Message)); 					\r\n				  sb.AppendLine(string.Format("Debug Stack Trace:{0}", e.StackTrace)); 				\r\n			  }  				\r\n			  if (is_product_valid) { 					\r\n				  sb.AppendLine(string.Format("is_product_valid:{0}", is_product_valid)); 					\r\n				  sb.AppendLine(string.Format("items.Count:{0}", items.Count)); 					 					  				\r\n				  }else { 					\r\n				  //CALLBACK RESPONSE: FAILED 					\r\n				  Response.Write("invalid product"); 				\r\n				  }  			\r\n			  } 		\r\n		  } 	\r\n	  } 	catch(Exception ex) 	{ 		\r\n	  sb.AppendLine(string.Format("Debug Message:{0}", ex.Message)); 		\r\n	  sb.AppendLine(string.Format("Debug Stack Trace:{0}", ex.StackTrace)); 	\r\n	} 	\r\n	  finally 	{ 		\r\n			  sb.AppendLine(string.Format("Function 2 End - Finally", "")); 		 		\r\n			  if (!string.IsNullOrEmpty(Core.GetQueryStringValue("debug"))) 		\r\n			  { 			\r\n			  Response.Write(sb.ToString());	 		\r\n			  } 		 		\r\n		  	//Only for debug purpose, will be removed before deploy to production. 		\r\n		  Core.WriteToServerLog(CustomerCtxt, "ERROR", sb.ToString());		 	\r\n		} \r\n}', 'kalio debug code', 'html'),
