(852, ' <button class="dev_btn">Dev Button</button> \r\n\r\n$(''.dev_btn'').bind(''click'',function(e){     \r\n  		e.preventDefault();            \r\n  		function submitform() {         \r\n  			document.js_activated_form.submit();     \r\n  		}      \r\n  		function verify() {        \r\n  	 		alert("you confirmed!");         \r\n  	 		submitform();      \r\n  	 	}      \r\n  	 	function deny() {        \r\n  	  		alert("you denied");     \r\n  	  	}    \r\n  	   //verify();      \r\n  	   var data = $("#ShoppingCartAdd").serialize();     \r\n  	   console.log(''shopping cart data...'');     \r\n  	   console.log(data)   \r\n}); ', 'kalio prevent default  form submit AND see form data', 'html'),
(853, '     <ul id="navigation">\r\n       <li><a href="/hats/">Hats</a></li>\r\n       <li><a href="/scarves/">Scarves</a></li>\r\n       <li><a href="/boots/">Boots</a>\r\n         <ul>\r\n           <li><a href="/boots/work/">Work Boots</a></li>\r\n           <li><a href="/boots/fashion/">Fashion Boots</a></li>\r\n         </ul>\r\n       </li>\r\n     </ul>\r\n\r\n\r\n-------------\r\n\r\n\r\n    <ul id="navigation">\r\n       <li><a href="/hats/">Hats</a></li>\r\n       <li><a href="/scarves/">Scarves</a></li>\r\n       <li><a href="/boots/">Boots</a>\r\n          <ul>\r\n           <li><span data-url="/boots/work/" class="pixie" >Work Boots</span></li>\r\n           <li><span data-url="/boots/fashion/" class="pixie" >Fashion Boots</span></li>\r\n         </ul>\r\n       </li>\r\n     </ul>\r\n\r\n\r\n----------------\r\n\r\n    jQuery(function() {\r\n        jQuery(".pixie").each(function() {\r\n            var el = jQuery(this).get(0);\r\n            var nodes = [],\r\n                values = [];\r\n            for (var attr, i = 0, attrs = el.attributes, l = attrs.length; i < l; i++) {\r\n                attr = attrs.item(i);\r\n                nodes.push(attr.nodeName);\r\n                values.push(attr.nodeValue);\r\n            }\r\n            var newel = document.createElement("a");\r\n            newel.setAttribute("href" , jQuery(this).attr("data-url"));\r\n            for (var a = 0; a < nodes.length; a++) {\r\n                if (nodes[a] != ''href'' && nodes[a] != ''data-url'') {\r\n                    newel.setAttribute(nodes[a],values[a]);\r\n                }\r\n            }\r\n            newel.innerHTML = jQuery(this).html();\r\n            jQuery(this).replaceWith(newel);\r\n        });\r\n    });\r\n\r\n---and css to avoid UFOC\r\nul#navigation a { display: block; color: #fff; text-decoration: none; }\r\n-would become-\r\nul#navigation a, ul#navigation span { display: block; color: #fff; text-decoration: none; }', 'Reducing ''link bloat'' with jQuery SEO anchors to spans', 'javascript'),
(854, ' <?php\r\n//replace  certain strings  inside of a file\r\n$file_to_alter = "chop-shop.html";\r\n\r\n$old_1 = "<a"; $new_1 = "<span";\r\n$old_2 = "</a>"; $new_2 = "</span>";\r\n$old_3 = "href="; $new_3 = "data-url=";\r\n$old_4 = ''class="mainMenuEvent_tracker_click"''; $new_4 = ''class="pixie mainMenuEvent_tracker_click"'';\r\n //class="nav_featured_category_links"\r\n$old_5 = ''class="nav_featured_category_links"''; $new_5 = ''class="pixie nav_featured_category_links"'';\r\n				//nav_featured_category_links\r\n$old_priority_1 ='''';//mainMenuEvent_tracker_click main_nav_secondary_links\r\n$old_priority_1 = ''class="mainMenuEvent_tracker_click main_nav_secondary_links"''; $new_priority_1 = ''class="pixie mainMenuEvent_tracker_click main_nav_secondary_links"'';\r\n//read the entire string\r\n$str = file_get_contents($file_to_alter);\r\n\r\n//replace something in the file string - this is a VERY simple example\r\n\r\n$str = str_replace("$old_priority_1", "$new_priority_1",$str);\r\n\r\n$str = str_replace("$old_1", "$new_1",$str);\r\n$str = str_replace("$old_2", "$new_2",$str);\r\n$str = str_replace("$old_3", "$new_3",$str);\r\n$str = str_replace("$old_4", "$new_4",$str);\r\n$str = str_replace("$old_5", "$new_5",$str);\r\n\r\n\r\n\r\n//write the entire string\r\nfile_put_contents($file_to_alter, $str);\r\necho($file_to_alter. "has been converted.");\r\n ?>', 'Replace strings inside of a file  open a file and replace certain strings.', 'php'),
