(868, 'app/phone-list/phone-list.template.html:\r\n\r\n<div class="container-fluid">\r\n  <div class="row">\r\n    <div class="col-md-2">\r\n      <!--Sidebar content-->\r\n\r\n      <p>\r\n        Search:\r\n        <input ng-model="$ctrl.query">\r\n      </p>\r\n\r\n      <p>\r\n        Sort by:\r\n        <select ng-model="$ctrl.orderProp">\r\n          <option value="name">Alphabetical</option>\r\n          <option value="age">Newest</option>\r\n        </select>\r\n      </p>\r\n\r\n    </div>\r\n    <div class="col-md-10">\r\n      <!--Body content-->\r\n\r\n      <ul class="phones">\r\n        <li ng-repeat="phone in $ctrl.phones | filter:$ctrl.query | orderBy:$ctrl.orderProp">\r\n          <span>{{phone.name}}</span>\r\n          <p>{{phone.snippet}}</p>\r\n        </li>\r\n      </ul>\r\n\r\n    </div>\r\n  </div>\r\n</div>\r\n\r\n//HERE IS THE MATCHING component\r\napp/phone-list/phone-list.component.js:\r\n\r\nangular.\r\n  module(''phoneList'').\r\n  component(''phoneList'', {\r\n    templateUrl: ''phone-list/phone-list.template.html'',\r\n    controller: function PhoneListController() {\r\n      this.phones = [\r\n        {\r\n          name: ''Nexus S'',\r\n          snippet: ''Fast just got faster with Nexus S.'',\r\n          age: 1\r\n        }, {\r\n          name: ''Motorola XOOMâ„¢ with Wi-Fi'',\r\n          snippet: ''The Next, Next Generation tablet.'',\r\n          age: 2\r\n        }, {\r\n          name: ''MOTOROLA XOOMâ„¢'',\r\n          snippet: ''The Next, Next Generation tablet.'',\r\n          age: 3\r\n        }\r\n      ];\r\n\r\n      this.orderProp = ''age'';\r\n    }\r\n  });', 'angularjs add a sort or filter to a search', 'javascript'),
(870, 'describe(''phoneList'', function() {\r\n\r\n  // Load the module that contains the `phoneList` component before each test\r\n  beforeEach(module(''phoneList''));\r\n\r\n  // Test the controller\r\n  describe(''PhoneListController'', function() {\r\n    var ctrl;\r\n\r\n    beforeEach(inject(function($componentController) {\r\n      ctrl = $componentController(''phoneList'');\r\n    }));\r\n\r\n    it(''should create a `phones` model with 3 phones'', function() {\r\n      expect(ctrl.phones.length).toBe(3);\r\n    });\r\n\r\n    it(''should set a default value for the `orderProp` model'', function() {\r\n      expect(ctrl.orderProp).toBe(''age'');\r\n    });\r\n\r\n  });\r\n\r\n});\r\n\r\n// E2E\r\ndescribe(''PhoneCat Application'', function() {\r\n\r\n  describe(''phoneList'', function() {\r\n\r\n    ...\r\n\r\n    it(''should be possible to control phone order via the drop-down menu'', function() {\r\n      var queryField = element(by.model(''$ctrl.query''));\r\n      var orderSelect = element(by.model(''$ctrl.orderProp''));\r\n      var nameOption = orderSelect.element(by.css(''option[value="name"]''));\r\n      var phoneNameColumn = element.all(by.repeater(''phone in $ctrl.phones'').column(''phone.name''));\r\n\r\n      function getNames() {\r\n        return phoneNameColumn.map(function(elem) {\r\n          return elem.getText();\r\n        });\r\n      }\r\n\r\n      queryField.sendKeys(''tablet'');   // Let''s narrow the dataset to make the assertions shorter\r\n\r\n      expect(getNames()).toEqual([\r\n        ''Motorola XOOM\\u2122 with Wi-Fi'',\r\n        ''MOTOROLA XOOM\\u2122''\r\n      ]);\r\n\r\n      nameOption.click();\r\n\r\n      expect(getNames()).toEqual([\r\n        ''MOTOROLA XOOM\\u2122'',\r\n        ''Motorola XOOM\\u2122 with Wi-Fi''\r\n      ]);\r\n    });', 'angularjs unit test and e2e test a filter or sorted search feature', 'javascript'),
(871, 'describe(''phoneList'', function() {\r\n\r\n  // Load the module that contains the `phoneList` component before each test\r\n  beforeEach(module(''phoneList''));\r\n\r\n  // Test the controller\r\n  describe(''PhoneListController'', function() {\r\n    var ctrl;\r\n\r\n    beforeEach(inject(function($componentController) {\r\n      ctrl = $componentController(''phoneList'');\r\n    }));\r\n\r\n    it(''should create a `phones` model with 3 phones'', function() {\r\n      expect(ctrl.phones.length).toBe(3);\r\n    });\r\n\r\n    it(''should set a default value for the `orderProp` model'', function() {\r\n      expect(ctrl.orderProp).toBe(''age'');\r\n    });\r\n\r\n  });\r\n\r\n});', 'angularjs and e2e test a filter or sorted search feature', 'javascript'),
