unit test for code in AngularJS. Since testing is such a critical part of software development, we make it easy to create tests in AngularJS so that developers are encouraged to write them.\r\n\r\n\r\napp/app.spec.js:\r\n\r\ndescribe(''PhoneListController'', function() {\r\n\r\n  beforeEach(module(''phonecatApp''));\r\n\r\n  it(''should create a `phones` model with 3 phones'', inject(function($controller) {\r\n    var scope = {};\r\n    var ctrl = $controller(''PhoneListController'', {$scope: scope});\r\n\r\n    expect(scope.phones.length).toBe(3);\r\n  }));\r\n\r\n});', 'angularjs basic MVC with unit test', 'javascript'),
(911, '<table border=1 width=100% >\r\n  <tr >\r\n  	<td ng-repeat="i in [0, 1, 2, 3, 4, 5, 6, 7]">COL:{{i+1}}</td>\r\n  </tr>\r\n  <tr ng-repeat="i in [0, 1, 2, 3, 4, 5, 6, 7]">\r\n  	<td ng-repeat="j in [0, 1, 2, 3, 4, 5, 6, 7]">{{j+1}}</td>\r\n  </tr>\r\n</table>', 'angularjs and build a table with ng-repeat loop', 'javascript'),
(912, '<table border=1 width=100% >\r\n  <tr >\r\n  	<td ng-repeat="i in [0, 1, 2, 3, 4, 5, 6, 7]">COL:{{i+1}}</td>\r\n  </tr>\r\n  <tr ng-repeat="i in [0, 1, 2, 3, 4, 5, 6, 7]">\r\n  	<td ng-repeat="j in [0, 1, 2, 3, 4, 5, 6, 7]">{{j+1}}</td>\r\n  </tr>\r\n</table>', 'angularjs and build a table with ng-repeat loop', 'javascript'),
(913, '/**\r\n * recommended\r\n *\r\n * no globals are left behind\r\n */\r\n\r\n// logger.js\r\n(function() {\r\n    ''use strict'';\r\n\r\n    angular\r\n        .module(''app'')\r\n        .factory(''logger'', logger);\r\n\r\n    function logger() { }\r\n})();\r\n\r\n// storage.js\r\n(function() {\r\n    ''use strict'';\r\n\r\n    angular\r\n        .module(''app'')\r\n        .factory(''storage'', storage);\r\n\r\n    function storage() { }\r\n})();', 'angularjs IIFE (Immediately Invoked Function Expression) example', 'javascript'),
(914, '/**\r\n * recommended\r\n *\r\n * no globals are left behind\r\n */\r\n\r\n// logger.js\r\n(function() {\r\n    ''use strict'';\r\n\r\n    angular\r\n        .module(''app'')\r\n        .factory(''logger'', logger);\r\n\r\n    function logger() { }\r\n})();\r\n\r\n// storage.js\r\n(function() {\r\n    ''use strict'';\r\n\r\n    angular\r\n        .module(''app'')\r\n        .factory(''storage'', storage);\r\n\r\n    function storage() { }\r\n})();', 'angularjs IIFE (Immediately Invoked Function Expression) example', 'javascript'),
