t({\r\n		interval: 150, // milliseconds delay before onMouseOver\r\n		over: drops_show,\r\n		timeout: 500, // milliseconds delay before onMouseOut\r\n		out: drops_hide\r\n	});\r\n	function drops_show(){ jQuery(this).addClass(''open''); }\r\n	function drops_hide(){ jQuery(this).removeClass(''open''); }\r\n*/\r\n\r\n\r\n\r\n	//----------------------------------\r\n	//Bootstrap Implementation: START\r\n	//----------------------------------\r\n		jQuery(document).on(''click'', function (e) {\r\n			//console.log("close all");\r\n			jQuery(".dropdown-full ").removeClass("open");\r\n		});\r\n\r\n		/*\r\n		jQuery("#navbar_id").on("click touchend", ".main_nav_link", function(e){\r\n			var thisDropdownStatus = jQuery(this).attr("aria-expanded");\r\n			var thisHref = jQuery(this).attr("href");\r\n			if (thisDropdownStatus == "true") {\r\n				window.location.href = thisHref;\r\n			}\r\n		});\r\n		*/\r\n\r\n		//Start Fix MegaNavbar on scroll page\r\n		if($(''#main_navbar'').length){\r\n				var navHeight = $(''#main_navbar'').offset().top;\r\n				FixMegaNavbar(navHeight);\r\n				$(window).bind(''scroll'', function() {FixMegaNavbar(navHeight);});\r\n		}\r\n		function FixMegaNavbar(navHeight) {\r\n			var pathname = jQuery.trim(window.location.pathname.replace("/", ""));\r\n\r\n			//Remove this feature for the cart page - per request of David Wilson and Bayon Forte, CC 2017\r\n			// if (pathname != ''cart.aspx'' && pathname != ''checkout.aspx'') {\r\n\r\n			if (pathname != ''checkout.aspx'') {\r\n				if (!$(''#main_navbar'').hasClass(''navbar-fixed-bottom'')) {\r\n					if ($(window).scrollTop() > navHeight) {\r\n						$(''#main_navbar'').addClass(''navbar-fixed-top'');\r\n						$(''#main_navbar'').css({''margin-top'':''57px''});\r\n						$(''body'').css({''margin-top'': $(''#main_navbar'').height()+''px''});\r\n						if ($(''#main_navbar'').parent(''div'').hasClass(''container'')) $(''#main_navbar'').children(''div'').addClass(''container'').removeClass(''container-fluid'');\r\n					