acking (''click'', ''Button'', ''Create Label'')\r\nfunction GoogleEventTracking(ga_listener, ga_category, ga_label) {\r\n	ga(''send'', ''event'', ga_category, ga_listener, ga_label);\r\n}\r\n\r\n//Check window position for sticky header\r\nfunction windowPositionCheck() {\r\n	if (jQuery(window).scrollTop() > 30) {\r\n		jQuery(''#top-links'').css({''position'': ''fixed'', ''margin-top'':''0px''});\r\n	} else if (jQuery(this).scrollTop() <= 30) {\r\n\r\n        if (jQuery(''#promo_code_banner_indicator'').length){\r\n            jQuery(''#top-links'').css({''position'': ''absolute'', ''margin-top'':''85px''});\r\n            //console.log("promo detected");\r\n        } else {\r\n            jQuery(''#top-links'').css({''position'': ''absolute'', ''margin-top'':''32px''});\r\n           // console.log("promo not detected");\r\n        }\r\n	}\r\n}\r\n\r\n//Update Cart Items Count in Sticky Header\r\nfunction updateHeaderCartItemCount(){\r\n	jQuery.ajax({\r\n		url: ''fn_ajax.aspx?fn=1'',\r\n		cache: false,\r\n		success: function(data) {\r\n			var thisHTML = data.split("<!DOCTYPE");\r\n			var thisData_string = jQuery.trim(thisHTML[0]);\r\n			var thisData_int = parseInt(thisData_string);\r\n\r\n			//SUCCESS\r\n			if(thisData_int > 1 || thisData_int == 0){\r\n				jQuery(''#cart_item_count'').html(thisData_string+'' items'');\r\n			}else {\r\n				jQuery(''#cart_item_count'').html(thisData_string+'' item'');\r\n			}\r\n\r\n		},\r\n\r\n		error: function(data, textStatus) {\r\n			//ERROR\r\n			alert(''AJAX ERROR: '' + textStatus);\r\n		}\r\n	});\r\n}\r\n\r\n//FN: Bootstrap breaking points\r\n/*\r\n	w >= 1200:	LG\r\n	w >= 992:	MD\r\n	w >= 768:	SM\r\n	w <  768:	XS\r\n*/\r\nfunction checkWindowSize() {\r\n	if (typeof matchMedia !== ''undefined'' && jQuery.isFunction(matchMedia)) {\r\n		//Phones\r\n		if (window.matchMedia("(max-width: 767px)").matches) {\r\n			//console.log("XS VIEWPORT");\r\n\r\n			//Set href for main menu items\r\n			jQuery(''.main_nav_link '').each(function()