itDirective,\r\n    ngNonBindableDirective,\r\n    ngPluralizeDirective,\r\n    ngRepeatDirective,\r\n    ngShowDirective,\r\n    ngStyleDirective,\r\n    ngSwitchDirective,\r\n    ngSwitchWhenDirective,\r\n    ngSwitchDefaultDirective,\r\n    ngOptionsDirective,\r\n    ngTranscludeDirective,\r\n    ngModelDirective,\r\n    ngListDirective,\r\n    ngChangeDirective,\r\n    requiredDirective,\r\n    requiredDirective,\r\n    ngValueDirective,\r\n    ngAttributeAliasDirectives,\r\n    ngEventDirectives,\r\n\r\n    $AnchorScrollProvider,\r\n    $AnimateProvider,\r\n    $BrowserProvider,\r\n    $CacheFactoryProvider,\r\n    $ControllerProvider,\r\n    $DocumentProvider,\r\n    $ExceptionHandlerProvider,\r\n    $FilterProvider,\r\n    $InterpolateProvider,\r\n    $IntervalProvider,\r\n    $HttpProvider,\r\n    $HttpBackendProvider,\r\n    $LocationProvider,\r\n    $LogProvider,\r\n    $ParseProvider,\r\n    $RootScopeProvider,\r\n    $QProvider,\r\n    $$SanitizeUriProvider,\r\n    $SceProvider,\r\n    $SceDelegateProvider,\r\n    $SnifferProvider,\r\n    $TemplateCacheProvider,\r\n    $TimeoutProvider,\r\n    $$RAFProvider,\r\n    $$AsyncCallbackProvider,\r\n    $WindowProvider\r\n*/\r\n\r\n\r\n/**\r\n * @ngdoc object\r\n * @name angular.version\r\n * @module ng\r\n * @description\r\n * An object that contains information about the current AngularJS version. This object has the\r\n * following properties:\r\n *\r\n * - `full` Ã¢â‚¬â€œ `{string}` Ã¢â‚¬â€œ Full version string, such as "0.9.18".\r\n * - `major` Ã¢â‚¬â€œ `{number}` Ã¢â‚¬â€œ Major version number, such as "0".\r\n * - `minor` Ã¢â‚¬â€œ `{number}` Ã¢â‚¬â€œ Minor version number, such as "9".\r\n * - `dot` Ã¢â‚¬â€œ `{number}` Ã¢â‚¬â€œ Dot version number, such as "18".\r\n * - `codeName` Ã¢â‚¬â€œ `{string}` Ã¢â‚¬â€œ Code name of the release, such as "jiggling-armfat".\r\n */\r\nvar version = {\r\n  full: ''1.2.16'',    // all of these placeholder strings will be replaced by grunt''s\r\n  major: 1,    // package task\r\n  minor: 2,\r\n  dot: 16,\r\n  codeName: ''badger-enumeration''\r\n};\r\n\r\n\r\nfunction publishExternalAPI(angular){\r\n  extend(angular, {\r\n    ''bootstrap'': bootstrap,\r\n    ''copy'': copy,\r\n    ''extend'': extend,\r\n    ''equals'': equals,\r\n    ''element'': jqLite,\r\n    ''forEach'': forEach,\r\n    ''injector'': createInjector,\r\n    ''noop'':noop,\r\n    ''bind'':bind,\r\n    ''toJson'': toJson,\r\n    ''fromJson'': fromJson,\r\n    ''identity'':identity,\r\n    ''isUndefined'': isUndefined,\r\n    ''isDefined'': isDefined,\r\n    ''isString'': isString,\r\n    ''isFunction'': isFunction,\r\n    ''isObject'': isObject,\r\n    ''isNumber'': isNumber,\r\n    ''isElement'': isElement,\r\n    ''isArray'': isArray,\r\n    ''version'': version,\r\n    ''isDate'': isDate,\r\n    ''lowercase'': lowercase,\r\n    ''uppercase'': uppercase,\r\n    ''callbacks'': {counter: 0},\r\n    ''$$minErr'': minErr,\r\n    ''$$csp'': csp\r\n  });\r\n\r\n  angularModule = setupModuleLoader(window);\r\n  try {\r\n    angularModule(''ngLocale'');\r\n  } catch (e) {\r\n    angularModule(''ngLocale'', []).provider(''$locale'', $LocaleProvider);\r\n  }\r\n\r\n  angularModule(''ng'', [''ngLocale''], [''$provide'',\r\n    function ngModule($provide) {\r\n      // $$sanitizeUriProvider needs to be before $compileProvider as it is used by it.\r\n      $provide.provider({\r\n        $$sanitizeUri: $$SanitizeUriProvider\r\n      });\r\n      $provide.provider(''$compile'', $CompileProvider).\r\n        directive({\r\n            a: htmlAnchorDirective,\r\n            input: inputDirective,\r\n            textarea: inputDirective,\r\n            form: formDirective,\r\n            script: scriptDirective,\r\n            select: selectDirective,\r\n            style: styleDirective,\r\n            option: optionDirective,\r\n            ngBind: ngBindDirective,\r\n            ngBindHtml: ngBindHtmlDirective,\r\n            ngBindTemplate: ngBindTemplateDirective,\r\n            ngClass: ngClassDirective,\r\n            ngClassEven: ngClassEvenDirective,\r\n            ngClassOdd: ngClassOddDirective,\r\n            ngCloak: ngCloakDirective,\r\n            ngController: ngControllerDirective,\r\n            ngForm: ngFormDirective,\r\n            ngHide: ngHideDirective,\r\n            ngIf: ngIfDirective,\r\n            ngInclude: ngIncludeDirective,\r\n            ngInit: ngInitDirective,\r\n            ngNonBindable: ngNonBindableDirective,\r\n            ngPluralize: ngPluralizeDirective,\r\n            ngRepeat: ngRepeatDirective,\r\n            ngShow: ngShowDirective,\r\n            ngStyle: ngStyleDirective,\r\n            ngSwitch: ngSwitchDirective,\r\n            ngSwitchWhen: ngSwitchWhenDirective,\r\n            ngSwitchDefault: ngSwitchDefaultDirective,\r\n            ngOptions: ngOptionsDirective,\r\n            ngTransclude: ngTranscludeDirective,\r\n            ngModel: ngModelDirective,\r\n            ngList: ngListDirective,\r\n            ngChange: ngChangeDirective,\r\n            required: requiredDirective,\r\n            ngRequired: requiredDirective,\r\n            ngValue: ngValueDirective\r\n        }).\r\n        directive({\r\n          ngInclude: ngIncludeFillContentDirective\r\n        }).\r\n        directive(ngAttributeAliasDirectives).\r\n        directive(ngEventDirectives);\r\n      $provide.provider({\r\n        $anchorScroll: $AnchorScrollProvider,\r\n        $animate: $AnimateProvider,\r\n        $browser: $BrowserProvider,\r\n        $cacheFactory: $CacheFactoryProvider,\r\n        $controller: $ControllerProvider,\r\n        $document: $DocumentProvider,\r\n        $exceptionHandler: $ExceptionHandlerProvider,\r\n        $filter: $FilterProvider,\r\n        $interpolate: $InterpolateProvider,\r\n        $interval: $IntervalProvider,\r\n        $http: $HttpProvider,\r\n        $httpBackend: $HttpBackendProvider,\r\n        $location: $LocationProvider,\r\n        $log: $LogProvider,\r\n        $parse