(979, '.button {\r\n    display: block;\r\n    width: 115px;\r\n    height: 25px;\r\n    background: #4E9CAF;\r\n    padding: 10px;\r\n    text-align: center;\r\n    border-radius: 5px;\r\n    color: white;\r\n    font-weight: bold;\r\n}\r\n<a class="button">Add Problem</a>', 'anchor like a button', 'css'),
(980, '<!--  THE MODAL -->\r\n<div style=''margin-top:5%;'' class=''modal fade'' id=''modal-container-snips'' role=''dialog'' aria-labelledby=''myModalLabel'' aria-hidden=''false''>\r\n <div class=''modal-dialog''>\r\n   <div class=''modal-content''>\r\n     <div class=''modal-header''>\r\n     <!--	<button type=''button'' class=''close'' data-dismiss=''modal'' aria-hidden=''false''>\r\n         Ã—\r\n       </button>\r\n-->				<h4 class=''modal-title'' id=''myModalLabel''> Edit   </h4>\r\n     </div>\r\n     <div class=''modal-body''>\r\n       ...\r\n     </div>\r\n     <div class=''modal-footer''>\r\n       <button type=''button'' class=''btn btn-default'' data-dismiss=''modal''>\r\n         Close\r\n       </button>\r\n     </div>\r\n   </div>\r\n </div>\r\n</div>\r\n\r\n\r\n<!-- hidden anchor that calls the modal -->\r\n 				<a id=''modal-snips'' style='''' href=''#modal-container-snips'' role=''button'' class=''btn invisible'' data-toggle=''modal''>modal-1</a>\r\n\r\n        <script>\r\ndocument.getElementById(''modal-snips'').click();\r\n//build some html\r\nvar html = "<p>foo</p>";\r\n$(''.modal-body'').html(html);\r\n        </script>\r\n', 'bootstrap modal ', 'html'),
(981, '$(''.modal'').on(''shown.bs.modal'',function(){      //correct here use ''shown.bs.modal'' event which comes in bootstrap3\r\n  $(this).find(''iframe'').attr(''src'',''http://www.google.com'')\r\n})', 'add iframe rto bootstrap modal', 'javascript'),
(982, 'Much like what happens as part of github deploy, have a feature that you can supply a base-href that will be added to the index.html during the ng build command\r\n\r\nUsage : ng build --base-href <base>\r\nExample : ng build --base-href home\r\nReplaces <base href="/"> with <base href="/home"> in index.html', 'angular angular-cli build base-href', 'javascript'),
(983, '<!-- Gift Calendar Module: -->\r\n\r\n<!-- dependencies   -->\r\n<link href="https://cdnjs.cloudflare.com/ajax/libs/fullcalendar/2.6.1/fullcalendar.min.css" type="text/css" rel="stylesheet" />\r\n<script src="https://cdnjs.cloudflare.com/ajax/libs/moment.js/2.8.2/moment.min.js"></script>\r\n<script src="https://cdnjs.cloudflare.com/ajax/libs/fullcalendar/2.6.1/fullcalendar.min.js"></script>\r\n<!-- styles -->\r\n<style>\r\n      .giftCalendarContainer{\r\n        min-height:500px; width:500px;\r\n        margin-top:30px;\r\n        margin-bottom:30px;\r\n        border:solid 1px #ccc;\r\n        border-radius:4px;\r\n        padding:10px;\r\n      }\r\n</style>\r\n\r\n<!-- bootstrap modal -->\r\n<div id="fullCalModal" class="modal fade">\r\n    <div class="modal-dialog">\r\n        <div class="modal-content">\r\n            <div class="modal-header">\r\n                <button type="button" class="close" data-dismiss="modal"><span aria-hidden="true">Ã—</span> <span class="sr-only">close</span></button>\r\n                <h4 id="modalTitle" class="modal-title"></h4>\r\n            </div>\r\n            <div id="modalBody" class="modal-body"></div>\r\n            <div class="modal-footer">\r\n                <button type="button" class="btn btn-default" data-dismiss="modal">Close</button>\r\n                <!-- <a class="btn btn-primary" id="eventUrl" target="_blank">Event Page</a> -->\r\n            </div>\r\n        </div>\r\n    </div>\r\n</div>\r\n\r\n<dmi:loop dmisource=''parent''>\r\n<!-- calendar html -->\r\n <div class="col-xs-12 col-sm-12 col-md-12 col-lg-12 " style="min-height:200px;margin:0 !important;" >\r\n     <div id="container_<dmi:expression value=''((Product)Container.DataItem).ProductNumber.ToString()''></dmi:expression>" >\r\n       <div id="datepicker_<dmi:expression value=''((Product)Container.DataItem).ProductNumber.ToString()''></dmi:expression>" class="giftCalendarContainer"     >\r\n       </div>\r\n    </div>\r\n</div>\r\n\r\n<script>\r\n\r\nfunction fetchCalendarShipDates(  product_number, wk_num ) {\r\n     var events = null;\r\n\r\n    jQuery.ajax({\r\n        url: ''fn_ajax.aspx?fn=30&product_number='' + product_number + ''&week='' + wk_num,\r\n        cache: false,\r\n        success: function(data) {\r\n          // first we need real dates here\r\n           var thisHTML = data.split("<!DOCTYPE");\r\n           var thisData = thisHTML[0];\r\n           var jsonData = "["+thisData+ "]";\r\n           var listItems_json =   JSON.parse(jsonData)  ;\r\n\r\n          events = fullCalendarDayFormatter(listItems_json ,product_number);\r\n          callback(events);\r\n\r\n        },\r\n        error: function(data, textStatus) {\r\n            //ERROR\r\n            alert(''AJAX ERROR: '' + textStatus);\r\n        }\r\n    });\r\n\r\n}\r\n\r\nfunction callback(obj){\r\n   makeFullCalendar(obj);\r\n}\r\n\r\nfunction fullCalendarDayFormatter(data ,product_number){\r\n\r\n  var allDateData=[];\r\n    for (var i = 1; i < data.length; i++) {\r\n\r\n      obj = {};\r\n      obj.title = data[i][''calendarMethod''];\r\n      var d = new Date( data[i][''calendarDate'']);\r\n      obj.start =   moment( d.toISOString());\r\n      obj.end =     moment( d.toISOString());\r\n      obj.description  = data[i][''calendarDate''];\r\n      obj.allday = "false";\r\n      obj.url = "";\r\n      allDateData.push(obj);\r\n\r\n    }\r\n    return allDateData;\r\n}\r\n\r\nvar product_number = <dmi:expression value=''((Product)Container.DataItem).ProductNumber.ToString()''></dmi:expression>;\r\nvar wk_num = 1;\r\nvar d = new Date("2017-08-07");\r\nvar myJsonData = fetchCalendarShipDates( product_number,  wk_num);\r\n//console.log(''This returns UNDEFINED...BUT... the callback has what we need to make the fullCalendar'');\r\nfunction makeFullCalendar(eventsObj){\r\n  $(''#datepicker_<dmi:expression value=''((Product)Container.DataItem).ProductNumber.ToString()''></dmi:expression>'').fullCalendar({\r\n      header: {\r\n