(987, '<!DOCTYPE html>\r\n<html>\r\n\r\n<body>\r\n<script>\r\n/*\r\nPurpose: Compare a JSON object to an array of JSON objects and check for :\r\nduplicate object, changed object, or new object.(could check for mismatch as well)\r\nLessons Learned:\r\n1.) verify that what you are comparing really IS what you think you are comparing.\r\n  A.) drill down into series of arrays and objects to check for sure.\r\n2.) cmd break DOES work as expected.\r\n  A.) Make SURE you use BREAK in array if you only want to perform action ONCE.\r\n3.) Once the parameter is inside the function it STILL represents the original variable.\r\n4.) TRUST that GLOBAL vars REALLY ARE GLOBAL.\r\n5.) When looping, use a log to verify that you are BREAKING at right points and times.\r\n*/\r\nvar isDuplicate = false;\r\nvar isDateChange = false;\r\nvar isNew = false;\r\nvar loop = true;\r\n\r\nvar _obj    = [{"part1":"D"},{"part2":"99"}];\r\nvar _array  = [[{"part1":"A"},{"part2":"1"}],[{"part1":"B"},{"part2":"2"}],[{"part1":"C"},{"part2":"3"}]];\r\ncompare(_obj,_array);\r\n\r\nfunction compare(obj, arr) {\r\n\r\n   var count = arr.length;\r\n   //check for duplicate\r\n   for (var i = 0; i < count; i++) {\r\n     console.log(''check dupe'');\r\n       //compare json objects\r\n       if (JSON.stringify(arr[i]) === JSON.stringify(obj)) {\r\n           isDuplicate = true;\r\n           isNew = false;\r\n           break;\r\n       }\r\n   }\r\n   //either Date Change or New\r\n   if (!isDuplicate) {\r\n       // loop check for Date Change\r\n       for (var j = 0; j < count; j++) {\r\n         console.log(''check date change'');\r\n\r\n           if (arr[j][0].part1 == obj[0].part1 ) {\r\n             console.log(''date change'');\r\n               isDateChange = true;\r\n               //replace element in an array\r\n               if (j !== -1) {\r\n\r\n                    arr[j]=obj;\r\n                    console.log(''poof'');\r\n                }\r\n                isNew = false;\r\n               break;\r\n           }else{\r\n             //assume it is new UNLESS it proves to be a ''Date Change''\r\n             isNew = true;\r\n           }\r\n       }\r\n   }\r\n   if(isNew){\r\n     arr.push(obj);\r\n   }\r\n\r\n   console.log(''___State___'');\r\n   console.log(''isDuplicate:'', isDuplicate);\r\n   console.log(''isDateChange:'', isDateChange);\r\n   console.log(''isNew:'', isNew);\r\n   console.log(_array);\r\n}\r\n</script>\r\n</body>\r\n</html>\r\n', 'Compare a JSON object to an array of JSON objects and check for :\r\nduplicate object, changed object, or new object.', 'javascript'),
