(1009, 'angularjs code loop through json', '<script>\r\n    var myApp = angular.module(''myApp'', []);\r\n\r\n    myApp.controller(''myController'', [''$scope'', ''$filter'', function ($scope, $filter) {\r\n\r\n        // ''employees'' OBJECT, WITH AN ARRAY OF DATA.\r\n        $scope.employees = {\r\n            ''05/17/2015'': { ''name'': ''Alpha'', ''age'': 37 },\r\n            ''06/25/2016'': { ''name'': ''Bravo'', ''age'': 27 },\r\n            ''09/11/2016'': { ''name'': ''Charlie'', ''age'': 29 },\r\n            ''01/17/2017'': { ''name'': ''Delta'', ''age'': 19 },\r\n            ''03/09/2014'': { ''name'': ''Echo'', ''age'': 32 }\r\n        }\r\n\r\n        // ITERATE THROUGH ITEMS IN OBJECT.\r\n        angular.forEach($scope.employees, function (value, key) {\r\n            var d = new Date(''01/05/2016'');         // DATE TO COMPARE (A SPECIFIED DATE).\r\n            d = $filter(''date'')(d, ''shortDate'');\r\n\r\n            var d1 = new Date();\r\n            d1 = $filter(''date'')(key, ''shortDate'');\r\n\r\n            //CHECK IF THE DATE IN OBJECT IS GREATER THAN OR EQUAL TO THE SPECIFIED DATE.\r\n            if (Date.parse(d1) >= Date.parse(d)) {\r\n                // SHOW THE EXTRACTED DATA.\r\n                console.log(key + ": " + value.name + ": " + value.age);\r\n            }\r\n        });\r\n    } ]\r\n    );\r\n</script>', 'javascript'),
(1010, '<!DOCTYPE html>\r\n<html>\r\n<head>\r\n  <meta name="viewport" content="width=device-width, initial-scale=1">\r\n  <link rel="stylesheet" href="https://maxcdn.bootstrapcdn.com/bootstrap/3.3.7/css/bootstrap.min.css">\r\n  <script src="https://ajax.googleapis.com/ajax/libs/jquery/3.2.1/jquery.min.js"></script>\r\n  <script src="https://maxcdn.bootstrapcdn.com/bootstrap/3.3.7/js/bootstrap.min.js"></script>\r\n<style>\r\n\r\n.dropdown-submenu {\r\n    position: relative;\r\n}\r\n\r\n.dropdown-submenu .dropdown-menu {\r\n    top: 25;\r\n    left: 25%;\r\n    margin-top: -1px;\r\n}\r\n</style>\r\n</head>\r\n<body>\r\n\r\n<div class="container">\r\n  <h2>Multi-Level Dropdowns</h2>\r\n  <p>In this example, we have created a .dropdown-submenu class for multi-level dropdowns (see style section above).</p>\r\n  <p>Note that we have added jQuery to open the multi-level dropdown on click (see script section below).</p>\r\n  <div class="dropdown">\r\n    <button class="btn btn-default dropdown-toggle" type="button" data-toggle="dropdown">Tutorials\r\n    <span class="caret"></span></button>\r\n    <ul class="dropdown-menu">\r\n      <li><a tabindex="-1" href="#">HTML</a></li>\r\n      <li><a tabindex="-1" href="#">CSS</a></li>\r\n      \r\n      <li class="dropdown-submenu">\r\n        <a class="test" tabindex="-1" href="#">New dropdown <span class="caret"></span></a>\r\n        <ul class="dropdown-menu">\r\n          <li><a tabindex="-1" href="#">2nd level dropdown</a></li>\r\n          <li><a tabindex="-1" href="#">2nd level dropdown</a></li>\r\n          <li class="dropdown-submenu">\r\n            <a class="test" href="#">Another dropdown <span class="caret"></span></a>\r\n            <ul class="dropdown-menu">\r\n              <li><a href="#">3rd level dropdown</a></li>\r\n              <li><a href="#">3rd level dropdown</a></li>\r\n            </ul>\r\n          </li>\r\n        </ul>\r\n      </li>\r\n      \r\n    </ul>\r\n  </div>\r\n</div>\r\n\r\n\r\n<script>\r\n$(document).ready(function(){\r\n  $(''.dropdown-submenu a.test'').on("click", function(e){\r\n    $(this).next(''ul'').toggle();\r\n    e.stopPropagation();\r\n    e.preventDefault();\r\n  });\r\n});\r\n</script>\r\n\r\n</body>\r\n</html>\r\n', 'bootstrap dropdown sub menus submenu', 'html'),
(1011, '/////////////////////A Wordpress Plugin Ajax Example////////////////////////////\r\nadd_action( ''admin_footer'', ''my_action_javascript'' ); // Write our JS below here\r\n\r\nfunction my_action_javascript() { ?>\r\n	<script type="text/javascript" >\r\n	jQuery(document).ready(function($) {\r\n\r\n		var data = {\r\n			''action'': ''my_action'',\r\n			''whatever'': 1234\r\n		};\r\n\r\n		// since 2.8 ajaxurl is always defined in the admin header and points to admin-ajax.php\r\n		jQuery.post(ajaxurl, data, function(response) {\r\n			alert(''Got this from the server: '' + response);\r\n		});\r\n	});\r\n	</script> <?php\r\n}\r\n\r\nadd_action( ''wp_ajax_my_action'', ''my_action'' );\r\n\r\nfunction my_action() {\r\n	global $wpdb; // this is how you get access to the database\r\n\r\n	$whatever = intval( $_POST[''whatever''] );\r\n\r\n	$whatever += 10;\r\n\r\n        echo $whatever;\r\n\r\n	wp_die(); // this is required to terminate immediately and return a proper response\r\n}\r\n///////////////////////////////////////////////////////////////////////', 'wordpress ajax in plugin ', 'php'),
(1012, 'wp add_meta_box ', '\r\n<?php\r\n////////////////////////////////////////////////////////////////////////////\r\n// add_meta_box( $id, $title, $callback, $page, $context, $priority, $callback_args ); \r\nadd_action( ''add_meta_boxes'', ''cd_meta_box_add'' );\r\nfunction cd_meta_box_add()\r\n{\r\n    add_meta_box( ''my-meta-box-id'', ''My First Meta Box'', ''cd_meta_box_cb'', ''post'', ''normal'', ''high'' );\r\n}\r\n function cd_meta_box_cb()\r\n{\r\n    // $post is already set, and contains an object: the WordPress post\r\n \r\n    $values = get_post_custom( $post->ID );\r\n    $text = isset( $values[''my_meta_box_text''] ) ? $values[''my_meta_box_text''] : '''';\r\n    $selected = isset( $values[''my_meta_box_select''] ) ? esc_attr( $values[''my_meta_box_select''] ) : '''';\r\n    $check = isset( $values[''my_meta_box_check''] ) ? esc_attr( $values[''my_meta_box_check''] ) : '''';\r\n     \r\n    // We''ll use this nonce field later on when saving.\r\n    wp_nonce_field( ''my_meta_box_nonce'', ''meta_box_nonce'' );\r\n    ?>\r\n    <p>\r\n        <label for="my_meta_box_text">Text Label</label>\r\n        <input type="text" name="my_meta_box_text" id="my_meta_box_text" value="<?php echo $text; ?>" />\r\n    </p>\r\n     \r\n    <p>\r\n        <label for="my_meta_box_select">Color</label>\r\n        <select name="my_meta_box_select" id="my_meta_box_select">\r\n            <option value="red" <?php selected( $selected, ''red'' ); ?>>Red</option>\r\n            <option value="blue" <?php selected( $selected, ''blue'' ); ?>>Blue</option>\r\n        </select>\r\n    </p>\r\n     \r\n    <p>\r\n        <input type="checkbox" id="my_meta_box_check" name="my_meta_box_check" <?php checked( $check, ''on'' ); ?> />\r\n        <label for="my_meta_box_check">Do not check this</label>\r\n    </p>\r\n    <?php  \r\n      // global $post;\r\n  // echo(''<pre>''); var_dump($post); echo(''</pre>'');\r\n}\r\n?>\r\n<?php\r\nadd_action( ''save_post'', ''cd_meta_box_save'' );\r\nfunction cd_meta_box_save( $post_id )\r\n{\r\n    \r\n        global $post;\r\n        echo(''<pre>''); var_dump($post); echo(''</pre>'');die();\r\n  \r\n    \r\n}\r\n?>', 'php'),
(1013, '<?php\r\n// ajax example forntend  originally thei scod ewent in the functions file\r\n/*\r\nand the javascript file it referes to looks like this:\r\njQuery(document).ready(function($) {\r\n    console.log(''on load this script is making an ajax call...and it is using the MyAjax object...'',MyAjax);\r\n  var data = {\r\n    action: ''my_action'',\r\n    security : MyAjax.security,\r\n    whatever: 1234\r\n  };\r\n    console.log(''request data:'',data);\r\n  // since 2.8 ajaxurl is always defined in the admin header and points to admin-ajax.php\r\n  $.post(MyAjax.ajaxurl, data, function(response) {\r\n    alert(''Got this from the server: '' + response);\r\n    console.log(''response data:'',response);\r\n  });\r\n});\r\n\r\n*/\r\n// Ajax Example: Add the JS\r\nfunction ajax_example_scripts() {\r\n  wp_enqueue_script( ''ajax-script-name'', get_template_directory_uri() . ''/js/ajax-script-example.js'', array(''jquery''), ''1.0.0'', true );\r\n  wp_localize_script( ''ajax-script-name'', ''MyAjax'', array(\r\n    // URL to wp-admin/admin-ajax.php to process the request\r\n    ''ajaxurl'' => admin_url( ''admin-ajax.php'' ),\r\n\r\n    // generate a nonce with a unique ID "myajax-post-comment-nonce"\r\n    // so that you can check it later when an AJAX request is sent\r\n    ''security'' => wp_create_nonce( ''my-special-string'' )\r\n  ));\r\n}\r\nadd_action( ''wp_enqueue_scripts'', ''ajax_example_scripts'' );\r\n\r\n// The function that handles the AJAX request\r\nfunction my_action_callback() {\r\n  check_ajax_referer( ''my-special-string'', ''security'' );\r\n\r\n  $whatever = intval( $_POST[''whatever''] );\r\n  $whatever += 10;\r\n  echo $whatever;\r\n  die(); // this is required to return a proper result\r\n}\r\nadd_action( ''wp_ajax_my_action'', ''my_action_callback'' );\r\n\r\n ?>\r\n', 'wp ajax on frontend basic example', 'php'),
(1014, '<style>\r\n.multi-item-carousel{\r\n  .carousel-inner{\r\n    > .item{\r\n      transition: 500ms ease-in-out left;\r\n    }\r\n    .active{\r\n      &.left{\r\n        left:-33%;\r\n      }\r\n      &.right{\r\n        left:33%;\r\n      }\r\n    }\r\n    .next{\r\n      left: 33%;\r\n    }\r\n    .prev{\r\n      left: -33%;\r\n    }\r\n    @media all and (transform-3d), (-webkit-transform-3d) {\r\n      > .item{\r\n        // use your favourite prefixer here\r\n        transition: 500ms ease-in-out left;\r\n        transition: 500ms ease-in-out all;\r\n        backface-visibility: visible;\r\n        transform: none!important;\r\n      }\r\n    }\r\n  }\r\n  .carouse-control{\r\n    &.left, &.right{\r\n      background-image: none;\r\n    }\r\n  }\r\n}\r\n\r\n// non-related styling:\r\nbody{\r\n  background: #333;\r\n  color: #ddd;\r\n}\r\nh1{\r\n  color: white;\r\n  font-size: 2.25em;\r\n  text-align: center;\r\n  margin-top: 1em;\r\n  margin-bottom: 2em;\r\n  text-shadow: 0px 2px 0px rgba(0, 0, 0, 1);\r\n}\r\n</style>\r\n<body>\r\n  <div class="container">\r\n    <h1>Use Bootstrap''s carousel to show multiple items per slide.</h1>\r\n    <div class="row">\r\n      <div class="col-md-12">\r\n        <div class="carousel slide multi-item-carousel" id="theCarousel">\r\n          <div class="carousel-inner">\r\n            <div class="item active">\r\n              <div class="col-xs-4"><a href="#1"><img src="http://placehold.it/300/f44336/000000" class="img-responsive"></a></div>\r\n            </div>\r\n            <div class="item">\r\n              <div class="col-xs-4"><a href="#1"><img src="http://placehold.it/300/e91e63/000000" class="img-responsive"></a></div>\r\n            </div>\r\n            <div class="item">\r\n              <div class="col-xs-4"><a href="#1"><img src="http://placehold.it/300/9c27b0/000000" class="img-responsive"></a></div>\r\n            </div>\r\n            <div class="item">\r\n              <div class="col-xs-4"><a href="#1"><img src="http://placehold.it/300/673ab7/000000" class="img-responsive"></a></div>\r\n            </div>\r\n            <div class="item">\r\n              <div class="col-xs-4"><a href="#1"><img src="http://placehold.it/300/4caf50/000000" class="img-responsive"></a></div>\r\n            </div>\r\n            <div class="item">\r\n              <div class="col-xs-4"><a href="#1"><img src="http://placehold.it/300/8bc34a/000000" class="img-responsive"></a></div>\r\n            </div>\r\n            <!-- add  more items here -->\r\n            <!-- Example item start:  -->\r\n\r\n            <div class="item">\r\n              <div class="col-xs-4"><a href="#1"><img src="http://placehold.it/300/8bc34a/000000" class="img-responsive"></a></div>\r\n            </div>\r\n\r\n            <!--  Example item end -->\r\n          </div>\r\n          <a class="left carousel-control" href="#theCarousel" data-slide="prev"><i class="glyphicon glyphicon-chevron-left"></i></a>\r\n          <a class="right carousel-control" href="#theCarousel" data-slide="next"><i class="glyphicon glyphicon-chevron-right"></i></a>\r\n        </div>\r\n      </div>\r\n    </div>\r\n  </div>\r\n  <script>\r\n  // Instantiate the Bootstrap carousel\r\n  $(''.multi-item-carousel'').carousel({\r\n    interval: false\r\n  });\r\n\r\n  // for every slide in carousel, copy the next slide''s item in the slide.\r\n  // Do the same for the next, next item.\r\n  $(''.multi-item-carousel .item'').each(function(){\r\n    var next = $(this).next();\r\n    if (!next.length) {\r\n      next = $(this).siblings('':first'');\r\n    }\r\n    next.children('':first-child'').clone().appendTo($(this));\r\n\r\n    if (next.next().length>0) {\r\n      next.next().children('':first-child'').clone().appendTo($(this));\r\n    } else {\r\n    	$(this).siblings('':first'').children('':first-child'').clone().appendTo($(this));\r\n    }\r\n  });\r\n  </script>\r\n  <body>\r\n', 'bootstrap carousel multiple items per slide', 'html'),
