(1019, 'function asi_animate_post($type,$del=.5,$dur=.9){\r\n	//$entry_animation_type = "fadeInLeftBig";\r\n\r\n	///////////////////////////////////////////\r\n	/*\r\n	//requires: plugin AnimateIt!\r\n	//purpose: wrap the next block of divs in this animation.\r\n	// files: \r\n		index.php\r\n			code: based on $index and $using_animation, call content or content-alt\r\n		tempate-parts/post/content.php\r\n			code: insert function call before block of code you want to animate.\r\n		template-parts/post/content-alt.php\r\n			code: insert function call before block of code you want to animate.\r\n		functions.php\r\n			code: function asi_animate_post($type,$del=.5,$dur=.9);\r\n	///////////////////////////////////////////\r\n	*/\r\n	$animation_args=   ''[edsanimate_start \r\n							entry_animation_type= "''.$type.''" \r\n							entry_delay= "''.$del.''" \r\n							entry_duration= "''.$dur.''" \r\n							entry_timing= "cubic-bezier(0.455, 0.03, 0.515, 0.955)" \r\n							exit_animation_type= "" \r\n							exit_delay= "" \r\n							exit_duration= "" \r\n							exit_timing= "" \r\n							animation_repeat= "1" \r\n							keep= "yes" \r\n							animate_on= "scroll" \r\n							scroll_offset= "15" \r\n							custom_css_class= ""\r\n							]'';\r\n\r\n		//echo out the shortcode, created by AnimateIt! \r\n		echo(do_shortcode($animation_args)); \r\n\r\n}', 'wp wordpress function to animate posts using AnimateIt! plugin', 'php'),
(1020, '<script type="text/javascript">\r\n <!-- \r\nvar $ = jQuery; \r\n$(document).ready(function(){ \r\n    console.log(''jquery...''); \r\n    \r\n    console.log(''asdf nonsense'');\r\n}); \r\n \r\n// -->\r\n</script>', 'javascript in Wordpress WP or jquery in WP', 'javascript'),
(1021, '// Database Structure \r\nCREATE TABLE ''webpage_details'' (\r\n ''link'' text NOT NULL,\r\n ''title'' text NOT NULL,\r\n ''description'' text NOT NULL,\r\n ''internal_link'' text NOT NULL,\r\n) ENGINE=MyISAM AUTO_INCREMENT=5 DEFAULT CHARSET=latin1\r\n\r\n<?php\r\n $main_url="http://samplesite.com";\r\n $str = file_get_contents($main_url);\r\n \r\n // Gets Webpage Title\r\n if(strlen($str)>0)\r\n {\r\n  $str = trim(preg_replace(''/\\s+/'', '' '', $str)); // supports line breaks inside <title>\r\n  preg_match("/\\<title\\>(.*)\\<\\/title\\>/i",$str,$title); // ignore case\r\n  $title=$title[1];\r\n }\r\n	\r\n // Gets Webpage Description\r\n $b =$main_url;\r\n @$url = parse_url( $b );\r\n @$tags = get_meta_tags($url[''scheme''].''://''.$url[''host''] );\r\n $description=$tags[''description''];\r\n	\r\n // Gets Webpage Internal Links\r\n $doc = new DOMDocument; \r\n @$doc->loadHTML($str); \r\n \r\n $items = $doc->getElementsByTagName(''a''); \r\n foreach($items as $value) \r\n { \r\n  $attrs = $value->attributes; \r\n  $sec_url[]=$attrs->getNamedItem(''href'')->nodeValue;\r\n }\r\n $all_links=implode(",",$sec_url);\r\n \r\n // Store Data In Database\r\n $host="localhost";\r\n $username="root";\r\n $password="";\r\n $databasename="sample";\r\n $connect=mysql_connect($host,$username,$password);\r\n $db=mysql_select_db($databasename);\r\n\r\n mysql_query("insert into webpage_details values(''$main_url'',''$title'',''$description'',''$all_links'')");\r\n\r\n?>', 'simple php web crawler ', 'php'),
(1022, '<?php if (is_front_page()) {\r\n	if (!isset($_COOKIE["notacarrot"])) {\r\n		setcookie("notacarrot", "beentheredonethat", time()+31500000);\r\n	}\r\n  } ?>\r\n\r\n\r\n//condition to check...\r\n<?php if ($_COOKIE["notacarrot"] == "beentheredonethat") { ?>\r\n	// DO SOMETHING\r\n<?php } ?>', 'wordpress set a cookie  users first visit set time to keep cookie...', 'php'),
