(1025, 'udemy_crud.js.txt\r\n//////////////////////////////////////////////////////\r\nfunctions.php \r\n//~REGISTER RESTful FIELD\r\nfunction custom_rest() {\r\n  register_rest_field(''post'', ''authorName'', array(\r\n    ''get_callback'' => function() {return get_the_author();}\r\n  ));\r\n\r\n  register_rest_field(''note'', ''userNoteCount'', array(\r\n    ''get_callback'' => function() {return count_user_posts(get_current_user_id(), ''note'');}\r\n  ));\r\n}\r\n\r\nadd_action(''rest_api_init'', ''custom_rest'');\r\n\r\n/*\r\n//~ CREATE GLOBAL NONCE VARIABLE:(place this code inside the function that includes your scripts)\r\n\r\n    wp_enqueue_script(''main-university-js'', get_theme_file_uri(''/js/scripts-bundled.js''), NULL, ''1.0'', true);\r\n\r\n 	wp_localize_script(''main-membership-js'', ''membershipData'', array(\r\n      ''root_url'' => get_site_url(),\r\n      ''nonce'' => wp_create_nonce(''wp_rest'')\r\n    ));\r\n\r\n\r\n*/\r\n\r\n\r\n///////////////////////////////////////////////////////////\r\nmu-plugins/custom_post_types.php\r\n<?php\r\n//~ CREATE CUSTOM POST TYPE\r\nfunction custom_post_types() {\r\n\r\n  // Note Post Type\r\n  register_post_type(''note'', array(\r\n    ''show_in_rest'' => true,\r\n    ''supports'' => array(''title'', ''editor''),\r\n    ''public'' => false,\r\n    ''show_ui'' => true,\r\n    ''labels'' => array(\r\n      ''name'' => ''Notes'',\r\n      ''add_new_item'' => ''Add New Note'',\r\n      ''edit_item'' => ''Edit Note'',\r\n      ''all_items'' => ''All Notes'',\r\n      ''singular_name'' => ''Note''\r\n    ),\r\n    ''menu_icon'' => ''dashicons-welcome-write-blog''\r\n  ));\r\n\r\n\r\n}\r\n\r\nadd_action(''init'', ''custom_post_types'');\r\n\r\n\r\n////////////////////////////////////////////////////////////\r\nscripts.js\r\n//~ DEFINE jQuery and IMPORT JAVASCRIPT CRUD CLASS\r\n// 3rd party packages from NPM\r\nimport $ from ''jquery'';\r\n// Our modules / classes\r\nimport MyNotes from ''./modules/MyNotes'';\r\n// Instantiate a new object using our modules/classes\r\nvar mynotes = new MyNotes();\r\n//////////////////////////////////////////////////////////\r\nMyNotes.js\r\nimport $ from ''jquery'';\r\n//~CREATE JAVASCRIPT CRUD CLASS with EVENT LISTENERS and CRUD methods\r\n/*\r\nRelated Files:\r\npage-my-notes.php  contains the id #my-notes \r\n*/\r\nclass MyNotes {\r\n  constructor() {\r\n    this.events();\r\n    this.newFormIsVisible = false;\r\n    this.newForm = $(''#newForm'');\r\n  }\r\n\r\n  events() {\r\n    $("#toggleNewForm").on("click",    this.toggleNew.bind(this) );\r\n\r\n    $("#placeholderDataBtn").on("click",    this.placeholderData.bind(this) );\r\n\r\n    $("#my-notes").on("click", ".delete-note", this.deleteNote);\r\n    $("#my-notes").on("click", ".edit-note", this.editNote.bind(this));\r\n    $("#my-notes").on("click", ".update-note", this.updateNote.bind(this));\r\n    $(".submit-note").on("click", this.createNote.bind(this));\r\n  }\r\n\r\n  // Methods will go here\r\n  editNote(e) {\r\n    var thisNote = $(e.target).parents("li");\r\n    if (thisNote.data("state") == "editable") {\r\n      this.makeNoteReadOnly(thisNote);\r\n    } else {\r\n      this.makeNoteEditable(thisNote);\r\n    }\r\n  }\r\n\r\n  makeNoteEditable(thisNote) {\r\n    thisNote.find(".edit-note").html(''<i class="fa fa-times" aria-hidden="true"></i> Cancel'');\r\n    thisNote.find(".note-title-field, .note-body-field").removeAttr("readonly").addClass("note-active-field");\r\n    thisNote.find(".update-note").addClass("update-note--visible");\r\n    thisNote.data("state", "editable");\r\n  }\r\n\r\n  makeNoteReadOnly(thisNote) {\r\n    thisNote.find(".edit-note").html(''<i class="fa fa-pencil" aria-hidden="true"></i> Edit'');\r\n    thisNote.find(".note-title-field, .note-body-field").attr("readonly", "readonly").removeClass("note-active-field");\r\n    thisNote.find(".update-note").removeClass("update-note--visible");\r\n    thisNote.data("state", "cancel");\r\n  }\r\n\r\n // method to toggle hide/show the NEW NOTE FORM\r\n  toggleNew(e) {\r\n    console.log(''Two Properties in the constructor, one bound event , one method, and a UI button.'');\r\n    var thisNewNote = $(e.target);\r\n     if(this.newFormIsVisible){\r\n       //$(this.newForm).addClass(''hide'');\r\n       $(this.newForm).slideUp();\r\n\r\n     }else{\r\n       //$(this.newForm).removeClass(''hide'');\r\n       $(this.newForm).slideDown();\r\n    }\r\n    this.newFormIsVisible =! this.newFormIsVisible;\r\n  }\r\n\r\n\r\nplaceholderData(e){\r\n\r\n      var root = ''https://jsonplaceholder.typicode.com'';\r\n\r\n      $.ajax({\r\n        url: root + ''/posts/'',\r\n        method: ''GET''\r\n      }).then(function(data) {\r\n        console.log(data);\r\n      });\r\n\r\n\r\n}\r\n\r\n//~ CRUD Methods\r\n\r\n\r\n  deleteNote(e) {\r\n    var thisNote = $(e.target).parents("li");\r\n\r\n    $.ajax({\r\n      beforeSend: (xhr) => {\r\n        xhr.setRequestHeader(''X-WP-Nonce'', membershipData.nonce);\r\n      },\r\n      url: membershipData.root_url + ''/wp-json/wp/v2/note/'' + thisNote.data(''id''),\r\n      type: ''DELETE'',\r\n      success: (response) => {\r\n        thisNote.slideUp();\r\n        console.log("Congrats");\r\n        console.log(response);\r\n        if (response.userNoteCount < 10) {\r\n          $(".note-limit-message").removeClass("active");\r\n        }\r\n      },\r\n      error: (response) => {\r\n        console.log("Sorry 1");\r\n        console.log(response);\r\n      }\r\n    });\r\n  }\r\n\r\n  updateNote(e) {\r\n    var thisNote = $(e.target).parents("li");\r\n\r\n    var ourUpdatedPost = {\r\n      ''title'': thisNote.find(".note-title-field").val(),\r\n      ''content'': thisNote.find(".note-body-field").val()\r\n    }\r\n    \r\n    $.ajax({\r\n      beforeSend: (xhr) => {\r\n        xhr.setRequestHeader(''X-WP-Nonce'', membershipData.nonce);\r\n      },\r\n      url: membershipData.root_url + ''/wp-json/wp/v2/note/'' + thisNote.data(''id''),\r\n      type: ''POST'',\r\n      data: ourUpdatedPost,\r\n      success: (response) => {\r\n        this.makeNoteReadOnly(thisNote);\r\n        console.log("Congrats");\r\n        console.log(response);\r\n      },\r\n      error: (response) => {\r\n        console.log("Sorry 2");\r\n        console.log(response);\r\n      }\r\n    });\r\n  }\r\n\r\n  createNote(e) {\r\n    var ourNewPost = {\r\n      ''title'': $(".new-note-title").val(),\r\n      ''content'': $(".new-note-body").val(),\r\n      ''status'': ''publish''\r\n    }\r\n    \r\n    $.ajax({\r\n      beforeSend: (xhr) => {\r\n        xhr.setRequestHeader(''X-WP-Nonce'', membershipData.nonce);\r\n      },\r\n      url: membershipData.root_url + ''/wp-json/wp/v2/note/'',\r\n      type: ''POST'',\r\n      data: ourNewPost,\r\n      success: (response) => {\r\n        $(".new-note-title, .new-note-body").val('''');\r\n        $(`\r\n          <li data-id="${response.id}">\r\n            <input readonly class="note-title-field" value="${response.title.raw}">\r\n            <span class="edit-note"><i class="fa fa-pencil" aria-hidden="true"></i> Edit</span>\r\n            <span class="delete-note"><i class="fa fa-trash-o" aria-hidden="true"></i> Delete</span>\r\n            <textarea readonly class="note-body-field">${response.content.raw}</textarea>\r\n            <span class="update-note btn btn--blue btn--small"><i class="fa fa-arrow-right" aria-hidden="true"></i> Save</span>\r\n          </li>\r\n          `).prependTo("#my-notes").hide().slideDown();\r\n\r\n        console.log("Congrats");\r\n        console.log(response);\r\n      },\r\n      error: (response) => {\r\n        console.log(''The response of createing a note and reaching the limit...''+response);\r\n        if(response.responseText == "You have reached your note limit.") {\r\n          $(".note-limit-message").addClass("active");\r\n        }\r\n        console.log("Sorry 3");\r\n        console.log(response);\r\n      }\r\n    });\r\n  }\r\n}\r\n\r\nexport default MyNotes;\r\n\r\n///////////////////////////////////////////////////////\r\npage-my-notes.php\r\n \r\n\r\n<?php\r\n\r\n//prevent hijacking the URL with /my-notes\r\n  if (!is_user_logged_in()) {\r\n    wp_redirect(esc_url(site_url(''/'')));\r\n    exit;\r\n  }\r\n\r\n  get_header();\r\n\r\n  while(have_posts()) {\r\n    the_post();\r\n    //pageBanner();\r\n     ?>\r\n    \r\n    \r\n\r\n    <div id="my-notes-page" class="container container--narrow page-section">\r\n      \r\n      <span" ><button  id="toggleNewForm" class="btn btn--green" >New Note(child)</button></span>\r\n       <span id="placeholderDataBtn" class="btn btn--blue" >PlaceHolder Data</span>\r\n      \r\n      <div id="newForm" class="create-note hide">\r\n        <h2 class="headline headline--medium">Create New Note</h2>\r\n        <input class="new-note-title" placeholder="Title">\r\n        <textarea class="new-note-body" placeholder="Your note here..."></textarea>\r\n        <span class="submit-note">Create Note</span>\r\n        <span class="note-limit-message">Note limit reached: delete an existing note to make room for a new one.</span>\r\n      </div>\r\n      <!-- ||||||||||||||      THE MAIN ID #my-notes  refered to js/modules/MyNotes.js  ||||||||||||   -->\r\n      <ul class="min-list link-list" id="my-notes">\r\n        <?php \r\n          $userNotes = new WP_Query(array(\r\n            ''post_type'' => ''note'',\r\n            ''posts_per_page'' => -1,\r\n            ''author'' => get_current_user_id()\r\n          ));\r\n\r\n          while($userNotes->have_posts()) {\r\n            $userNotes->the_post(); ?>\r\n\r\n            <li data-id="<?php the_ID(); ?>">\r\n\r\n              <input \r\n              readonly \r\n              class="note-title-field" \r\n              value="<?php echo str_replace(''Private: '', '''', esc_attr(get_the_title())); ?>"\r\n              >\r\n\r\n              <span class="edit-note">\r\n                  <i class="fa fa-pencil" aria-hidden="true"></i> \r\n                  Edit\r\n              </span>\r\n             \r\n              <span class="delete-note">\r\n                  <i class="fa fa-trash-o" aria-hidden="true"></i> \r\n                  Delete\r\n              </span>\r\n\r\n              <textarea readonly class="note-body-field">\r\n                  <?php echo esc_textarea(get_the_content()); ?>\r\n              </textarea>\r\n\r\n              <span class="update-note btn btn--blue btn--small">\r\n                  <i class="fa fa-arrow-right" aria-hidden="true"></i> \r\n                  Save\r\n              </span>\r\n\r\n            </li>\r\n\r\n          <?php }\r\n\r\n        ?>\r\n      </ul>\r\n        \r\n    </div>\r\n    \r\n     \r\n  <?php }\r\n\r\n \r\n  get_footer();\r\n\r\n?>\r\n//////////////////////////////////////////////////////////////////\r\n\r\n   \r\n', 'WP udemy CRUD a collection/concatenation of files related to the course', 'php'),
(1026, '<?php\r\n/*\r\nPlugin Name: AM Popular Posts\r\nPlugin URI: http://awfulmedia.com\r\nDescription: aDD THE AWFULmEDIA pOPULAR POSTS WIDGET TRO TYYOUR THEME\r\nVersion: 1.0\r\nAuthor: auSTIN    fly\r\nAuthor URI: http://awfulmedai.com\r\nLicense: none\r\n*/\r\n\r\nclass am_popular_posts extends WP_Widget {\r\n	\r\n	function __construct(){\r\n		parent::__construct(false,$name=__(''AM Popular Posts''));\r\n	}	\r\n	function form(){\r\n\r\n	}\r\n	function update(){\r\n		\r\n	}\r\n	function widget($args, $instance){\r\n		?>\r\n			<div class="widget popular-posts one-thrid column">\r\n				<h2>Popular Stuff</h2>\r\n				<ul>\r\n					<?php\r\n						$query_args = array(\r\n							''post_type'' => ''post'',\r\n							''posts_per_page'' => 5,\r\n							''orderby'' => ''meta_value_num'',\r\n							''meta-key'' => ''post_views_count''\r\n						);\r\n						$query = new WP_Query($query_args);\r\n						while($query->have_posts()){\r\n						  $query->the_post();\r\n							?>\r\n							<li><a href="<?php the_permalink(); ?>"><?php the_title(); ?> </a></li>\r\n							<?php\r\n							 } \r\n							?>\r\n\r\n					 \r\n					\r\n				</ul>\r\n			</div>\r\n		<?php\r\n		\r\n	}\r\n}\r\n\r\nadd_action(''widgets_init'', function() {\r\n	register_widget(''am_popular_posts'');\r\n});\r\n\r\n?>', 'WP Widget simple custom widget as plugin', 'php'),
