(1047, '<!-- header.php -->\r\n<nav id="site-navigation" class="main-navigation">\r\n	 <a class="toggle-nav" href="#" style="width:90%;text-align:right;">&#9776;</a>\r\n\r\n	<?php\r\n	wp_nav_menu( array(\r\n		''theme_location'' => ''menu-1'',\r\n		''menu_id''        => ''primary-menu'',\r\n	) );\r\n	?>\r\n	\r\n</nav><!-- #site-navigation -->\r\n<!-- functions.php -->\r\n\r\nfunction wpmu_burger_menu_scripts() {\r\n    \r\n    wp_enqueue_script( ''burger-menu-script'', get_theme_root_uri() . ''/_score-child/assets/js/burger-menu-script.js'', array( ''jquery'' ) );\r\n \r\n}\r\nadd_action( ''wp_enqueue_scripts'', ''wpmu_burger_menu_scripts'' );\r\n\r\n<!-- burger-menu-script.js -->\r\njQuery(document).ready(function() {\r\n    jQuery(''.toggle-nav'').click(function(e) {\r\n        jQuery(''#site-navigation ul'').slideToggle(500);\r\n 		//console.log(''trying to...'');\r\n        e.preventDefault();\r\n    });\r\n    \r\n});\r\n\r\n<!-- style.css -->\r\n/*note: this css needs to be widdled down */\r\n#site-navigation { \r\n	position:fixed;\r\n	top:0px;\r\n	right:0px;\r\n	width:70%;\r\n}\r\nnav.main-navigation{\r\n	float:left;\r\n} \r\nul.nav-menu{\r\n 	margin-top:0px;\r\n 	background-color:#fff;\r\n}\r\n.nav-menu li{\r\n 	margin-top:0px;\r\n 	margin-bottom:0px;\r\n}\r\nli.menu-item a{\r\n	color:#555;\r\n	line-height:3;\r\n	height: 95.375px;\r\n 	text-transform: uppercase; \r\n} \r\nli.menu-item a:hover{\r\n    background-color:#f26522;\r\n    color:#fff;\r\n}\r\n \r\n/* custom toggle burger menu */\r\n .toggle-nav {\r\n    display: none !important;\r\n}\r\n /* generated menu class */\r\n.current_page_item{  background-color:#f26522;color:#fff; } \r\n\r\n.current-cat{} \r\n\r\n.current-menu-item{} \r\n.current-menu-item a{color:#fff !important;} \r\n\r\n.menu-item-type-taxonomy{}\r\n\r\n.menu-item-type-post_type{} \r\n\r\n.menu-item-type-custom{} \r\n\r\n.menu-item-home{} \r\n/* end generated classes */  \r\n\r\n/* === RWD ============================ */ \r\n @media (mi