hover'');\r\n//////////////////////////////////////////////////////////////////\r\n\r\n\r\nfunction group_of_accordions_shortcode( $atts, $content = null ) {\r\n\r\n    $html = ''\r\n    <style>\r\n.accordion {\r\n    background-color: #eee;\r\n    color: #444;\r\n    cursor: pointer;\r\n    padding: 18px;\r\n    width: 100%;\r\n    border: none;\r\n    text-align: left;\r\n    outline: none;\r\n    font-size: 15px;\r\n    transition: 0.4s;\r\n}\r\n\r\n.active, .accordion:hover {\r\n    background-color: #ccc; \r\n}\r\n\r\n.panel {\r\n    padding: 0 18px;\r\n    display: none;\r\n    background-color: white;\r\n    overflow: hidden;\r\n}\r\n</style>\r\n<div style="width:100%;float:left;">'' . do_shortcode($content) . ''</div>\r\n    '';\r\n    $html .= ''\r\n\r\n<script>\r\nvar acc = document.getElementsByClassName("accordion");\r\nvar i;\r\n\r\nfor (i = 0; i < acc.length; i++) {\r\n    acc[i].addEventListener("click", function() {\r\n        this.classList.toggle("active");\r\n        var panel = this.nextElementSibling;\r\n        if (panel.style.display === "block") {\r\n            panel.style.display = "none";\r\n        } else {\r\n            panel.style.display = "block";\r\n        }\r\n    });\r\n}\r\n</script>\r\n    '';\r\n\r\n\r\n\r\n\r\n      return $html;\r\n      /*\r\n      USAGE:\r\n      [group_of_accordions][accordion title="Lorem" content="Lorem ipsum dolor sit amet, consectetur adipisicing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat."][accordion title="Ipsum"  content="Ipsum dolor sit amet, consectetur adipisicing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat."][accordion title="DOLOR"  content="Dolor sit amet, consectetur adipisicing elit, sed do eiusmod tempor incididunt ut lab