(281, 72, 'UPDATE SCRIPT:\r\nhttp://localhost/study/csv_massTicket.php\r\n&lt;?php\r\n\r\n$data = csv_to_array(&quot;Problem_Tickets.csv&quot;);\r\nmysql_connect(&quot;db1.smoothstone.com&quot;,&quot;generico&quot;,&quot;ghz765topwell9&quot;);\r\necho(mysql_error());\r\nforeach ($data as $key =&gt; $val) {\r\n	foreach ($val as $k =&gt; $v) {\r\n			 if($k==''Ticket #''){\r\n			 	echo(&quot;&lt;br&gt;&quot;.$v);\r\n				 $ticketNumber = $v;\r\n				  $sql = &quot;UPDATE `cid_play`.`tickets` SET `product_id`= 16 ,`rootCauseId`=538,`ticket_state_id`=1 WHERE ''tid'' =''&quot;.$v.&quot;''&quot; ;\r\n				//mysql_query($sql);\r\n				 echo(mysql_error());\r\n			 }\r\n	}\r\n}\r\n\r\n\r\nfunction csv_to_array($filename = '''', $delimiter = '','') {\r\n	if (!file_exists($filename) || !is_readable($filename))\r\n		return FALSE;\r\n	$header = NULL;\r\n	$data = array();\r\n	if (($handle = fopen($filename, ''r'')) !== FALSE) {\r\n		while (($row = fgetcsv($handle, 1000, $delimiter)) !== FALSE) {\r\n			if (!$header)\r\n				$header = $row;\r\n			else\r\n				$data[] = array_combine($header, $row);\r\n		}\r\n		fclose($handle);\r\n	}\r\n	return $data;\r\n}\r\n?&gt;'),
