(484, 115, 'Installs with Missing Preventative maintenance Information:\r\nSELECT \r\n    iid as ''Install ID'',\r\n    company.company_name as ''Company'',\r\n    location.location_name as ''Location Name'',\r\n    access_install_date as ''P1 Date'',\r\n    voice_install_date as ''P2 Date'',\r\n    contact as ''Contact Name'',\r\n    contactPhone as ''Contact Number'',\r\n    disasterPhone as ''Disaster Number'',\r\n    disasterEmail  as ''Disaster Email''\r\nFROM\r\n    installs\r\n    LEFT JOIN location USING (lid)\r\n    LEFT JOIN company ON (location.cid = company.cid)\r\n    LEFT JOIN disaster ON (location.lid = disaster.lid) \r\nWHERE\r\n    isid NOT IN (16,3,13)\r\n	AND company.cid != 1\r\n	AND company.inactive = 0\r\n	AND access_install_date &gt;= ''2014-01-01''\r\n	AND voice_install_date &gt;= ''2014-01-01''\r\n	AND access_install_date != ''0000-00-00''\r\n	AND voice_install_date != ''0000-00-00''\r\n    AND (\r\n        contact IS NULL\r\n        OR contact = ''''\r\n        OR contactPhone IS NULL\r\n        OR contactPhone = 0\r\n        OR disasterPhone IS NULL\r\n        OR disasterPhone = 0\r\n        OR disasterEmail IS NULL\r\n        OR disasterEmail = ''''\r\n    )\r\norder by company.company_name;'),
